export const APP_CONFIG = {
  name: process.env.NEXT_PUBLIC_APP_NAME || "Palpiteiros",
  defaultTheme: (process.env.NEXT_PUBLIC_DEFAULT_THEME as "light" | "dark") || "dark",
  gammaApiUrl: process.env.NEXT_PUBLIC_GAMMA_API_URL || "https://gamma-api.polymarket.com",
  supabaseUrl: process.env.NEXT_PUBLIC_SUPABASE_URL || "",
} as const;

// Market Types
export interface Market {
  id: string;
  question: string;
  description?: string;
  slug: string;
  outcomes: string[];
  tags: string[];
  endTime: string;
  liquidity?: number;
  volume?: number;
  probability?: number;
  status: "active" | "closed" | "pending";
  createdAt: string;
  updatedAt: string;
}

export interface Order {
  id: string;
  marketId: string;
  userId: string;
  outcome: string;
  type: "buy" | "sell";
  price: number;
  size: number;
  status: "pending" | "filled" | "cancelled";
  createdAt: string;
}

export interface Position {
  id: string;
  marketId: string;
  userId: string;
  outcome: string;
  size: number;
  avgPrice: number;
  unrealizedPnl: number;
  realizedPnl: number;
}

export interface User {
  id: string;
  email: string;
  username?: string;
  avatar?: string;
  balance: number;
  createdAt: string;
}

// API Response Types
export interface ApiResponse<T> {
  data: T;
  error?: string;
  message?: string;
}

export interface PaginatedResponse<T> {
  data: T[];
  total: number;
  page: number;
  pageSize: number;
  hasMore: boolean;
}

import { create } from "zustand";
import { Market } from "@/types";

interface MarketStore {
  markets: Market[];
  selectedMarket: Market | null;
  isLoading: boolean;
  error: string | null;
  setMarkets: (markets: Market[]) => void;
  setSelectedMarket: (market: Market | null) => void;
  setLoading: (loading: boolean) => void;
  setError: (error: string | null) => void;
}

export const useMarketStore = create<MarketStore>((set) => ({
  markets: [],
  selectedMarket: null,
  isLoading: false,
  error: null,
  setMarkets: (markets) => set({ markets }),
  setSelectedMarket: (market) => set({ selectedMarket: market }),
  setLoading: (loading) => set({ isLoading: loading }),
  setError: (error) => set({ error }),
}));

import { create } from "zustand";
import { User } from "@/types";

interface UserStore {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  setUser: (user: User | null) => void;
  setLoading: (loading: boolean) => void;
  logout: () => void;
}

export const useUserStore = create<UserStore>((set) => ({
  user: null,
  isAuthenticated: false,
  isLoading: true,
  setUser: (user) => set({ user, isAuthenticated: !!user }),
  setLoading: (loading) => set({ isLoading: loading }),
  logout: () => set({ user: null, isAuthenticated: false }),
}));

import { APP_CONFIG } from "@/config/app";
import { Market, PaginatedResponse } from "@/types";

class MarketService {
  private baseUrl = APP_CONFIG.gammaApiUrl;

  async getMarkets(params?: {
    limit?: number;
    offset?: number;
    tag?: string;
    search?: string;
  }): Promise<PaginatedResponse<Market>> {
    const searchParams = new URLSearchParams();
    if (params?.limit) searchParams.set("limit", params.limit.toString());
    if (params?.offset) searchParams.set("offset", params.offset.toString());
    if (params?.tag) searchParams.set("tag", params.tag);
    if (params?.search) searchParams.set("search", params.search);

    const response = await fetch(`${this.baseUrl}/markets?${searchParams}`);
    if (!response.ok) {
      throw new Error("Failed to fetch markets");
    }

    const data = await response.json();
    return {
      data: data.markets || [],
      total: data.total || 0,
      page: Math.floor((params?.offset || 0) / (params?.limit || 20)) + 1,
      pageSize: params?.limit || 20,
      hasMore: data.hasMore || false,
    };
  }

  async getMarketById(id: string): Promise<Market> {
    const response = await fetch(`${this.baseUrl}/markets/${id}`);
    if (!response.ok) {
      throw new Error("Failed to fetch market");
    }
    return response.json();
  }

  async getMarketPriceHistory(marketId: string): Promise<Array<{ timestamp: string; price: number }>> {
    const response = await fetch(`${this.baseUrl}/markets/${marketId}/price-history`);
    if (!response.ok) {
      throw new Error("Failed to fetch price history");
    }
    return response.json();
  }

  async getMarketOrders(marketId: string): Promise<any[]> {
    const response = await fetch(`${this.baseUrl}/markets/${marketId}/orders`);
    if (!response.ok) {
      throw new Error("Failed to fetch market orders");
    }
    return response.json();
  }
}

export const marketService = new MarketService();

import { supabase } from "@/config/supabase";
import { User } from "@/types";

class UserService {
  async getCurrentUser() {
    const { data: { user }, error } = await supabase.auth.getUser();
    if (error) throw error;
    return user;
  }

  async getUserProfile(userId: string): Promise<User> {
    const { data, error } = await supabase
      .from("users")
      .select("*")
      .eq("id", userId)
      .single();

    if (error) throw error;
    return data;
  }

  async updateUserProfile(userId: string, updates: Partial<User>) {
    const { data, error } = await supabase
      .from("users")
      .update(updates)
      .eq("id", userId)
      .select()
      .single();

    if (error) throw error;
    return data;
  }

  async signIn(email: string, password: string) {
    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });
    return { data, error };
  }

  async signUp(email: string, password: string) {
    const { data, error } = await supabase.auth.signUp({
      email,
      password,
    });
    return { data, error };
  }

  async signOut() {
    const { error } = await supabase.auth.signOut();
    return { error };
  }
}

export const userService = new UserService();

import { supabase } from "@/config/supabase";
import { Order } from "@/types";

class OrderService {
  async placeOrder(marketId: string, outcome: string, type: "buy" | "sell", price: number, size: number) {
    const { data: { user } } = await supabase.auth.getUser();
    if (!user) throw new Error("User not authenticated");

    const { data, error } = await supabase
      .from("orders")
      .insert({
        market_id: marketId,
        user_id: user.id,
        outcome,
        type,
        price,
        size,
        status: "pending",
      })
      .select()
      .single();

    if (error) throw error;
    return data;
  }

  async getUserOrders(userId: string): Promise<Order[]> {
    const { data, error } = await supabase
      .from("orders")
      .select("*")
      .eq("user_id", userId)
      .order("created_at", { ascending: false });

    if (error) throw error;
    return data || [];
  }

  async getMarketOrders(marketId: string): Promise<Order[]> {
    const { data, error } = await supabase
      .from("orders")
      .select("*")
      .eq("market_id", marketId)
      .order("created_at", { ascending: false });

    if (error) throw error;
    return data || [];
  }

  async cancelOrder(orderId: string) {
    const { data, error } = await supabase
      .from("orders")
      .update({ status: "cancelled" })
      .eq("id", orderId)
      .select()
      .single();

    if (error) throw error;
    return data;
  }
}

export const orderService = new OrderService();

import { useQuery } from "@tanstack/react-query";
import { marketService } from "@/services/market.service";
import { Market } from "@/types";

export function useMarkets(params?: {
  limit?: number;
  offset?: number;
  tag?: string;
  search?: string;
}) {
  return useQuery({
    queryKey: ["markets", params],
    queryFn: () => marketService.getMarkets(params),
    staleTime: 30000, // 30 seconds
  });
}

export function useMarket(id: string) {
  return useQuery({
    queryKey: ["market", id],
    queryFn: () => marketService.getMarketById(id),
    enabled: !!id,
    staleTime: 30000,
  });
}

export function useMarketPriceHistory(marketId: string) {
  return useQuery({
    queryKey: ["marketPriceHistory", marketId],
    queryFn: () => marketService.getMarketPriceHistory(marketId),
    enabled: !!marketId,
    staleTime: 60000, // 1 minute
  });
}

import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { userService } from "@/services/user.service";
import { useUserStore } from "@/stores/useUserStore";

export function useCurrentUser() {
  const { setUser, setLoading } = useUserStore();

  return useQuery({
    queryKey: ["currentUser"],
    queryFn: async () => {
      const user = await userService.getCurrentUser();
      setUser(user);
      return user;
    },
    staleTime: Infinity,
    retry: false,
  });
}

export function useSignIn() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: ({ email, password }: { email: string; password: string }) =>
      userService.signIn(email, password),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["currentUser"] });
    },
  });
}

export function useSignUp() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: ({ email, password }: { email: string; password: string }) =>
      userService.signUp(email, password),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["currentUser"] });
    },
  });
}

export function useSignOut() {
  const queryClient = useQueryClient();
  const { logout } = useUserStore();

  return useMutation({
    mutationFn: () => userService.signOut(),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["currentUser"] });
      logout();
    },
  });
}

import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
