/**
 * Test Supabase Database Connection
 *
 * This script tests:
 * 1. Basic connectivity to Supabase
 * 2. Query markets table
 * 3. Test RLS policies (if authenticated)
 * 4. Test real-time subscription
 */

import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;

async function testConnection() {
  console.log('üß™ Starting Supabase connection tests...\n');

  // Test 1: Create client
  console.log('Test 1: Creating Supabase client...');
  const supabase = createClient(supabaseUrl, supabaseAnonKey);
  console.log('‚úÖ Client created successfully\n');

  // Test 2: Basic connectivity
  console.log('Test 2: Testing basic connectivity...');
  const { data: versionData, error: versionError } = await supabase
    .rpc('version'); // This will fail if we can't connect

  if (versionError && versionError.code !== 'PGRST202') {
    console.log('‚úÖ Server is reachable (expected error for missing function)\n');
  } else {
    console.log('‚úÖ Server is responding\n');
  }

  // Test 3: Query markets table
  console.log('Test 3: Querying markets table...');
  const { data: markets, error: marketsError } = await supabase
    .from('markets')
    .select('*')
    .limit(1);

  if (marketsError) {
    console.error('‚ùå Failed to query markets:', marketsError.message);
    console.error('   This is expected if the database tables are not yet created.\n');
    console.log('   To create tables, run the migrations in supabase/migrations/\n');
    console.log('   You can run them via Supabase Dashboard > SQL Editor\n');
  } else {
    console.log('‚úÖ Successfully queried markets table');
    console.log(`   Found ${markets.length} market(s)`);
    if (markets.length > 0) {
      console.log('   Sample market:', markets[0].question);
    }
    console.log('');
  }

  // Test 4: Test RLS (attempt to insert without auth - should fail)
  console.log('Test 4: Testing RLS policies...');
  const { error: insertError } = await supabase
    .from('user_preferences')
    .insert({
      theme: 'dark',
      currency: 'USD',
      notifications_enabled: true,
      particle_effects: true,
      data_refresh_interval: 60000,
    });

  if (insertError) {
    console.log('‚úÖ RLS is working (insert rejected as expected)');
    console.log(`   Error: ${insertError.message}\n`);
  } else {
    console.log('‚ö†Ô∏è  Warning: RLS may not be properly configured\n');
  }

  // Test 5: Real-time subscription
  console.log('Test 5: Testing real-time subscription...');
  const channel = supabase
    .channel('test-market-prices')
    .on(
      'postgres_changes',
      {
        event: '*',
        schema: 'public',
        table: 'market_prices',
      },
      (payload) => {
        console.log('‚úÖ Real-time event received:', payload);
      }
    )
    .subscribe((status) => {
      if (status === 'SUBSCRIBED') {
        console.log('‚úÖ Successfully subscribed to market_prices');
        console.log('   (Unsubscribing after 2 seconds...)');
        setTimeout(() => {
          supabase.removeChannel(channel);
          console.log('‚úÖ Unsubscribed successfully\n');
        }, 2000);
      }
    });

  // Wait for subscription test
  await new Promise(resolve => setTimeout(resolve, 3000));

  console.log('‚úÖ All tests completed!');
  console.log('\nüìä Summary:');
  console.log('- Supabase URL:', supabaseUrl);
  console.log('- Anonymous Key:', supabaseAnonKey.substring(0, 20) + '...');
  console.log('- Tables may need to be created via migrations');
  console.log('- RLS policies are enforced');
  console.log('- Real-time subscriptions are working');
}

// Run tests
testConnection().catch(console.error);

/**
 * Apple-Inspired Design System for Polymarket Clone
 * Material Design Structure with Apple Aesthetic
 *
 * Design tokens organized into:
 * - Typography (SF Pro inspired)
 * - Colors (Semantic + Light/Dark modes)
 * - Spacing (8px base unit)
 * - Elevation (Apple shadows)
 * - Border Radius (Apple curves)
 * - Animation (Apple easing)
 * - Glassmorphism (Frosted glass)
 */

/* ============================================================================
   TYPOGRAPHY - Apple SF Pro Inspired
   ============================================================================ */

:root {
  /* Font Families */
  --font-sans: -apple-system, BlinkMacSystemFont, "SF Pro Display", "SF Pro Text",
    "Segoe UI", system-ui, sans-serif;
  --font-mono: "SF Mono", ui-monospace, Menlo, Monaco, "Cascadia Mono", monospace;

  /* Font Sizes - Apple's scale */
  --text-xs: 0.75rem; /* 12px */
  --text-sm: 0.875rem; /* 14px */
  --text-base: 1rem; /* 16px */
  --text-lg: 1.125rem; /* 18px */
  --text-xl: 1.25rem; /* 20px */
  --text-2xl: 1.5rem; /* 24px */
  --text-3xl: 1.875rem; /* 30px */
  --text-4xl: 2.25rem; /* 36px */
  --text-5xl: 3rem; /* 48px */
  --text-6xl: 3.75rem; /* 60px */
  --text-7xl: 4.5rem; /* 72px */
  --text-8xl: 6rem; /* 96px */
  --text-9xl: 8rem; /* 128px */

  /* Font Weights */
  --font-light: 300;
  --font-regular: 400;
  --font-medium: 500;
  --font-semibold: 600;
  --font-bold: 700;

  /* Line Heights */
  --leading-tight: 1.25;
  --leading-normal: 1.5;
  --leading-relaxed: 1.75;

  /* Letter Spacing */
  --tracking-tighter: -0.05em;
  --tracking-tight: -0.025em;
  --tracking-normal: 0;
  --tracking-wide: 0.025em;
  --tracking-wider: 0.05em;
  --tracking-widest: 0.1em;
}

/* ============================================================================
