  --color-dark-bg-tertiary: #334155; /* Slate 700 */

  --color-dark-fg-primary: #f8fafc; /* Slate 50 */
  --color-dark-fg-secondary: #e2e8f0; /* Slate 200 */
  --color-dark-fg-tertiary: #cbd5e1; /* Slate 300 */

  --color-dark-border: #334155; /* Slate 700 */
  --color-dark-border-hover: #475569; /* Slate 600 */

  /* Glassmorphism (Frosted Glass) */
  --glass-light-bg: rgba(255, 255, 255, 0.7);
  --glass-dark-bg: rgba(15, 23, 42, 0.7);
  --glass-blur: 10px;
}

/* ============================================================================
   SPACING - 8px Base Unit (Material Design)
   ============================================================================ */

:root {
  --spacing-0: 0;
  --spacing-px: 1px;
  --spacing-0_5: 0.125rem; /* 2px */
