  --modal-radius: var(--radius-2xl);
  --modal-padding: 2rem; /* 32px */
}

/**
 * Animation Curves - Apple-Inspired Easing Functions
 *
 * Provides TypeScript exports of cubic-bezier functions for programmatic use
 * in animations, transitions, and Framer Motion.
 *
 * @example
 * ```tsx
 * import { easeOut, spring } from '@/lib/animation-curves';
 *
 * // Use with CSS
 * const style = { transitionTimingFunction: easeOut };
 *
 * // Use with Framer Motion
 * <motion.div transition={{ ease: spring, duration: 0.3 }} />
 * ```
 */

/**
 * Cubic-bezier function for standard ease-out
 * Decelerates quickly at the start, slows down at the end
 * Best for: UI transitions, panel slides, fade outs
 */
export const easeOut = "cubic-bezier(0, 0, 0.2, 1)" as const;

/**
 * Cubic-bezier function for standard ease-in
 * Accelerates slowly at the start, speeds up at the end
 * Best for: Opening animations, entrance effects
 */
export const easeIn = "cubic-bezier(0.4, 0, 1, 1)" as const;

/**
 * Cubic-bezier function for standard ease-in-out
 * Smooth acceleration and deceleration
 * Best for: General purpose transitions, state changes
 */
export const easeInOut = "cubic-bezier(0.4, 0, 0.2, 1)" as const;

/**
 * Cubic-bezier function for spring animation
 * Overshoots slightly with bounce effect
 * Best for: Button presses, card interactions, playful animations
 */
export const spring = "cubic-bezier(0.175, 0.885, 0.32, 1.275)" as const;

/**
 * Cubic-bezier function for bounce effect
 * More pronounced bounce for emphasis
 * Best for: Attention-grabbing animations, success states
 */
export const bounce = "cubic-bezier(0.68, -0.55, 0.265, 1.55)" as const;

/**
 * Animation duration constants (in milliseconds)
 */
export const duration = {
  /** Instant transition (100ms) - hover states, micro-interactions */
  instant: 100,

  /** Fast transition (150ms) - button presses, toggle switches */
  fast: 150,

  /** Normal transition (300ms) - standard UI transitions, panel slides */
  normal: 300,

  /** Slow transition (500ms) - modal open/close, page transitions */
  slow: 500,

  /** Slower transition (700ms) - complex animations, multi-stage transitions */
  slower: 700,

  /** Slowest transition (1000ms) - hero animations, emphasis effects */
  slowest: 1000,
} as const;

/**
 * Pre-configured animation presets combining easing and duration
 */
export const preset = {
  /** Quick micro-interaction (button hover, toggle switch) */
  micro: {
    ease: easeOut,
    duration: duration.fast,
  },

  /** Standard UI transition (panel slide, modal open) */
  standard: {
    ease: easeInOut,
    duration: duration.normal,
  },

  /** Spring animation (button press, card interaction) */
  springy: {
    ease: spring,
    duration: duration.normal,
  },

  /** Bouncy emphasis (success state, notification) */
  bouncy: {
    ease: bounce,
    duration: duration.slow,
  },

  /** Smooth entrance (page load, hero animation) */
  entrance: {
    ease: easeOut,
    duration: duration.slow,
  },

  /** Gentle exit (modal close, panel dismiss) */
  exit: {
    ease: easeIn,
    duration: duration.fast,
  },
} as const;

/**
 * Type definitions for animation values
 */
export type AnimationCurve = typeof easeOut | typeof easeIn | typeof easeInOut | typeof spring | typeof bounce;
export type AnimationDuration = typeof duration[keyof typeof duration];
export type AnimationPreset = typeof preset[keyof typeof preset];

/**
 * Default animation configuration for Framer Motion
 */
export const defaultMotionConfig = {
  transition: {
    type: "tween" as const,
    ease: easeInOut,
    duration: duration.normal / 1000, // Convert to seconds
  },
} as const;

/**
 * Spring configuration for Framer Motion physics-based springs
 */
export const springConfig = {
  /** Gentle spring for subtle interactions */
  gentle: {
    type: "spring" as const,
    stiffness: 300,
    damping: 25,
  },

  /** Default spring for general use */
  default: {
    type: "spring" as const,
    stiffness: 400,
    damping: 20,
  },

  /** Bouncy spring for playful interactions */
  bouncy: {
    type: "spring" as const,
    stiffness: 500,
    damping: 15,
  },

  /** Stiff spring for precise movements */
  stiff: {
    type: "spring" as const,
    stiffness: 600,
    damping: 30,
  },
} as const;

/**
 * Elevation System - Apple + Material Design Shadows
 *
 * Provides TypeScript exports of elevation shadows for programmatic use.
 * Elevations create depth hierarchy and guide user attention.
 *
 * @example
 * ```tsx
 * import { elevation } from '@/lib/elevation-system';
 *
 * // Use in inline styles
 * <div style={{ boxShadow: elevation[2] }} />
 *
 * // Use with className
 * <div className="shadow-[var(--elevation-2)]" />
 * ```
 */

/**
 * Elevation shadow definitions
 * Matches Apple's subtle shadows with Material's depth system
 */
export const elevation = {
  /**
   * No shadow (flat surface)
   * Use for: Base elements, background content
   */
  0: "none" as const,

  /**
   * Subtle shadow (slight elevation)
   * Use for: Cards, list items, raised buttons
   */
  1: "0 1px 2px 0 rgb(0 0 0 / 0.05)" as const,

  /**
   * Light shadow (low elevation)
   * Use for: Hovered cards, dropdown menus, popovers
   */
  2: "0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1)" as const,

  /**
   * Medium shadow (moderate elevation)
   * Use for: Modals, raised cards, navigation bars
   */
  3: "0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1)" as const,

  /**
   * High shadow (significant elevation)
   * Use for: Active modals, focused panels, important cards
   */
  4: "0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1)" as const,

  /**
   * Very high shadow (maximum elevation)
   * Use for: Critical overlays, emphasized content, top-level modals
   */
  5: "0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1)" as const,

  /**
   * Extreme shadow (special elevation)
   * Use for: Hero elements, dramatic emphasis
   */
  6: "0 25px 50px -12px rgb(0 0 0 / 0.25)" as const,
} as const;

/**
 * Elevation level type
 */
export type ElevationLevel = keyof typeof elevation;

/**
 * Recommended elevation levels for common UI elements
 */
export const componentElevation = {
  /** Cards (default state) */
  card: elevation[1],

  /** Cards (hover state) */
  cardHover: elevation[2],

  /** Buttons (raised) */
  button: elevation[1],

  /** Buttons (hover) */
  buttonHover: elevation[2],

  /** Dropdown menus */
  dropdown: elevation[2],

  /** Popovers */
  popover: elevation[2],

  /** Modal backdrop */
  modalBackdrop: elevation[0],

  /** Modal content */
  modal: elevation[4],

  /** Navigation bar */
  navbar: elevation[2],

  /** Sidebar */
  sidebar: elevation[3],

  /** Tooltip */
  tooltip: elevation[3],

  /** Sticky header */
  stickyHeader: elevation[2],

  /** Table rows */
  tableRow: elevation[0],

  /** Table rows (hover) */
  tableRowHover: elevation[1],
} as const;

/**
 * Dynamic elevation class generator
 * Returns CSS class string for given elevation level
 *
 * @example
 * ```tsx
 * getElevationClass(2) // Returns "shadow-[0_1px_3px_0_rgb(0_0_0_/_0.1),_0_1px_2px_-1px_rgb(0_0_0_/_0.1)]"
 * ```
 */
export function getElevationClass(level: ElevationLevel): string {
  const shadow = elevation[level];
  // Convert CSS shadow to Tailwind arbitrary value format
  const formattedShadow = shadow
    .replace(/\s+/g, "_")
    .replace(/\//g, "_")
    .replace(/,/g, ",_")
    .replace(/rgb/g, "rgb");
  return `shadow-[${formattedShadow}]`;
}

/**
 * Interactive elevation hook (for React components)
 * Returns elevation classes for default and hover states
 *
 * @example
 * ```tsx
 * function Card() {
 *   const { base, hover } = useInteractiveElevation(1, 2);
 *   return <div className={base} onMouseEnter={() => setHover(hover)} />;
 * }
 * ```
 */
export function useInteractiveElevation(
  defaultLevel: ElevationLevel,
  hoverLevel: ElevationLevel
): { base: string; hover: string } {
  return {
    base: getElevationClass(defaultLevel),
    hover: getElevationClass(hoverLevel),
  };
}

/**
 * Z-index scale for layering
 * Pairs with elevation to establish complete depth hierarchy
 */
export const zIndex = {
  /** Base layer */
  base: 0,

  /** Sticky content */
  sticky: 10,

  /** Fixed navigation */
  fixed: 20,

  /** Dropdown menus */
  dropdown: 1000,

  /** Sticky header */
  stickyHeader: 1020,

  /** Fixed content */
  fixedContent: 1030,

  /** Modal backdrop */
  modalBackdrop: 1040,

  /** Modal content */
  modal: 1050,

  /** Popovers */
  popover: 1060,

  /** Tooltips */
  tooltip: 1070,

  /** Top layer */
  top: 9999,
} as const;

/**
 * Type definition for z-index
 */
export type ZIndexLevel = keyof typeof zIndex;

/**
 * Layering presets combining elevation and z-index
 */
export const layer = {
  /** Base content layer */
  base: {
    elevation: elevation[0],
    zIndex: zIndex.base,
  },

  /** Sticky navigation layer */
  sticky: {
    elevation: elevation[2],
    zIndex: zIndex.sticky,
  },

  /** Dropdown layer */
  dropdown: {
    elevation: elevation[2],
    zIndex: zIndex.dropdown,
  },

  /** Modal layer */
  modal: {
    elevation: elevation[4],
    zIndex: zIndex.modal,
  },

  /** Tooltip layer */
  tooltip: {
    elevation: elevation[3],
    zIndex: zIndex.tooltip,
  },
} as const;

