/**
 * Breaking Markets Page
 *
 * Server Component for the breaking markets page.
 * Displays markets with significant price movements in the last 24 hours.
 *
 * @features
 * - Server-side data fetching for initial load
 * - SEO metadata with Open Graph and Twitter cards
 * - Client Component wrapper for interactivity
 * - Real-time updates support
 *
 * @route /breaking
 */

import { Metadata } from 'next'
import { BreakingPageClient } from '@/components/breaking/breaking-page-client'
import { breakingService } from '@/services/breaking.service'
import type { BreakingMarket } from '@/types/breaking.types'

/**
 * Generate SEO metadata for the breaking markets page
 */
export async function generateMetadata(): Promise<Metadata> {
  return {
    title: 'Breaking Markets - Palpiteiros',
    description: 'See the prediction markets that moved the most in the last 24 hours. Real-time updates on the most dynamic markets with significant price movements.',
    keywords: ['breaking markets', 'prediction markets', 'polymarket', 'trading', 'price movement', 'volatility'],
    openGraph: {
      title: 'Breaking Markets - Palpiteiros',
      description: 'Real-time breaking markets - See the prediction markets that moved the most in the last 24 hours.',
      type: 'website',
      locale: 'en_US',
      url: 'https://palpiteiros.com/breaking',
      siteName: 'Palpiteiros',
    },
    twitter: {
      card: 'summary_large_image',
      title: 'Breaking Markets - Palpiteiros',
      description: 'Real-time breaking markets - See the prediction markets that moved the most.',
    },
    alternates: {
      canonical: '/breaking',
    },
  }
}

/**
 * Breaking Page Server Component
 *
 * Fetches initial breaking markets data on the server
 * and passes it to the client component for interactivity.
 *
 * @returns Breaking page with initial data
 */
export default async function BreakingPage() {
  // Fetch initial breaking markets data on server
  // This provides fast initial load and SEO content
  let initialMarkets: BreakingMarket[] = []
  let initialCount = 0

  try {
    const markets = await breakingService.getBreakingMarkets(
      {
        minPriceChange: 0.05,
        timeRange: '24h',
      },
      20 // Initial page size
    )
    initialMarkets = markets
    initialCount = markets.length
  } catch {
    // Silently fail - client component will handle error state
    initialMarkets = []
    initialCount = 0
  }

  return (
    <div className="min-h-screen bg-background">
      <BreakingPageClient
        initialMarkets={initialMarkets}
        initialCount={initialCount}
      />
    </div>
  )
}

/**
 * Breaking Page Client Component
 *
 * Client-side wrapper for the breaking markets page.
 * Manages filters, view mode, real-time updates, and infinite scroll.
 *
 * @features
 * - Client-side state management (filters, view mode)
 * - useBreakingMarkets hook for data fetching
 * - Real-time updates via Supabase subscriptions
 * - Infinite scroll pagination
 * - Loading states with skeleton cards
 * - Error states with retry button
 * - Grid/List view modes
 *
 * @component BreakingPageClient
 */

'use client'

import * as React from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { useBreakingMarkets, useBreakingRealtime } from '@/hooks/use-breaking-markets'
import { useBreakingStore } from '@/stores/breaking.store'
import { BreakingHeader } from './breaking-header'
import { BreakingFilters } from './breaking-filters'
import { BreakingList } from './breaking-list'
import { BreakingMarketCardSkeleton } from './breaking-market-card-skeleton'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip'
import { AlertCircle, RefreshCw, Wifi, WifiOff, Loader2 } from 'lucide-react'
import { cn } from '@/lib/utils'
import type { BreakingMarket } from '@/types/breaking.types'

/**
 * BreakingPageClient Props
 */
export interface BreakingPageClientProps {
  /** Initial markets from server-side fetch */
  initialMarkets: BreakingMarket[]
  /** Initial count of breaking markets */
  initialCount?: number
}

/**
 * BreakingPageClient Component
 *
 * Main client component for the breaking markets page.
 * Orchestrates header, filters, and markets list.
 *
 * @example
 * ```tsx
 * <BreakingPageClient
 *   initialMarkets={serverFetchedMarkets}
 *   initialCount={50}
 * />
 * ```
 */
export function BreakingPageClient({
  initialMarkets,
  initialCount = 0,
}: BreakingPageClientProps) {
  // Get filter and view state from Zustand store
  const {
    filters,
    setFilters,
    viewMode,
    setViewMode,
    setMarketsCache,
    updateMarketCache,
    isConnected,
    setConnected,
  } = useBreakingStore()

  // Local state for connection animation
  const [isConnecting, setIsConnecting] = React.useState(true)
  const [updatedMarketIds, setUpdatedMarketIds] = React.useState<Set<string>>(new Set())

  // Fetch breaking markets with current filters
  const {
    markets,
    isLoading,
    error,
    isRefetching,
    refetch,
  } = useBreakingMarkets({
    filters,
    limit: 50,
    enabled: true,
    refetchInterval: 60000, // Refresh every minute
  })

  // Real-time updates subscription
  const { isConnected: realtimeConnected } = useBreakingRealtime({
    enabled: true,
    onPriceChange: (market) => {
      // Update market in cache when price changes
      updateMarketCache(market)

      // Track updated markets for flash animation
      setUpdatedMarketIds((prev) => new Set(prev).add(market.id))

      // Clear from updated set after animation completes
      setTimeout(() => {
        setUpdatedMarketIds((prev) => {
          const next = new Set(prev)
          next.delete(market.id)
          return next
        })
      }, 1000)
    },
  })

  // Update connection state with animation
  React.useEffect(() => {
    if (realtimeConnected && isConnecting) {
      // Transition from connecting to connected
      const timer = setTimeout(() => setIsConnecting(false), 500)
      return () => clearTimeout(timer)
    } else if (!realtimeConnected && !isConnecting) {
      // Transition to disconnected state
      setIsConnecting(true)
      const timer = setTimeout(() => setIsConnecting(false), 500)
      return () => clearTimeout(timer)
    }
  }, [realtimeConnected, isConnecting])

  // Update cache when markets change
  React.useEffect(() => {
    if (markets.length > 0) {
      setMarketsCache(markets)
    }
  }, [markets, setMarketsCache])

  // Pagination state for infinite scroll
  const [displayCount, setDisplayCount] = React.useState(20)
  const displayedMarkets = React.useMemo(
    () => markets.slice(0, displayCount),
    [markets, displayCount]
  )

  /**
   * Handle filter changes
   */
  const handleFiltersChange = React.useCallback(
    (newFilters: Partial<typeof filters>) => {
      setFilters(newFilters)
      setDisplayCount(20) // Reset pagination on filter change
    },
    [setFilters]
  )

  /**
   * Handle retry after error
   */
  const handleRetry = React.useCallback(() => {
    refetch()
  }, [refetch])

  /**
   * Handle load more (for infinite scroll fallback)
   */
  const handleLoadMore = React.useCallback(() => {
    setDisplayCount((prev) => Math.min(prev + 20, markets.length))
  }, [markets.length])

  /**
   * Check if there are more markets to load
   */
  const hasMore = React.useMemo(
    () => displayCount < markets.length,
    [displayCount, markets.length]
  )

  /**
   * Get current date for header
   */
  const currentDate = React.useMemo(() => new Date(), [])

  /**
   * Container animation variants
   */
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.05,
      },
    },
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <BreakingHeader
        date={currentDate}
        marketsCount={markets.length}
        isConnected={isConnected}
        isRefetching={isRefetching}
      />

      {/* Filters */}
      <div className="mb-6">
        <BreakingFilters
          filters={filters}
          onFiltersChange={handleFiltersChange}
          viewMode={viewMode}
          onViewModeChange={setViewMode}
        />
      </div>

      {/* Error State */}
      <AnimatePresence mode="wait">
        {error && (
          <motion.div
            key="error"
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className="mb-6"
          >
            <Card variant="glass" className="border-destructive/50 bg-destructive/5">
              <CardContent className="p-4">
                <div className="flex items-center gap-3">
                  <AlertCircle className="h-5 w-5 text-destructive shrink-0" />
                  <div className="flex-1">
                    <p className="font-medium text-destructive">Failed to load breaking markets</p>
                    <p className="text-sm text-muted-foreground">{error.message}</p>
                  </div>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleRetry}
                    className="shrink-0"
                  >
                    <RefreshCw className="h-4 w-4 mr-2" />
                    Retry
                  </Button>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Markets List */}
      {isLoading && markets.length === 0 ? (
        // Loading skeleton
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className={cn(
            'grid gap-4',
            viewMode === 'grid' && 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3'
          )}
        >
          {Array.from({ length: 9 }).map((_, i) => (
            <BreakingMarketCardSkeleton key={i} viewMode={viewMode} />
          ))}
        </motion.div>
      ) : markets.length === 0 ? (
        // Empty state
        <Card variant="glass">
          <CardContent className="p-12 text-center">
            <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-muted mb-4">
              <AlertCircle className="h-8 w-8 text-muted-foreground" />
            </div>
            <h3 className="text-lg font-semibold mb-2">No breaking markets found</h3>
            <p className="text-muted-foreground mb-4">
              {filters.minPriceChange || filters.categories?.length
                ? 'Try adjusting your filters to see more results'
                : 'Markets with significant price movements will appear here'}
            </p>
            {filters.minPriceChange || filters.categories?.length ? (
              <Button
                variant="outline"
                onClick={() => handleFiltersChange({ minPriceChange: 0.05, categories: [] })}
              >
                Clear Filters
              </Button>
            ) : null}
          </CardContent>
        </Card>
      ) : (
        // Markets grid/list
        <BreakingList
          markets={displayedMarkets}
          viewMode={viewMode}
          onLoadMore={handleLoadMore}
          hasMore={hasMore}
          isLoading={isLoading}
        />
      )}

      {/* Connection Status Indicator */}
      <div className="fixed bottom-4 right-4 z-50">
        <Tooltip>
          <TooltipTrigger asChild>
            <motion.div
              initial={{ scale: 0, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              className={cn(
                'flex items-center gap-2 px-3 py-2 rounded-full text-xs font-medium',
                'backdrop-blur-md border shadow-lg cursor-help relative',
                isConnected
                  ? 'bg-green-500/10 border-green-500/20 text-green-500 dark:text-green-400'
                  : 'bg-red-500/10 border-red-500/20 text-red-500 dark:text-red-400'
              )}
            >
              {/* Connection icon with animations */}
              <div className="relative">
                {isConnecting ? (
                  <Loader2 className="h-3 w-3 animate-spin" />
                ) : isConnected ? (
                  <>
                    <Wifi className="h-3 w-3" />
                    {/* Pulsing green dot when connected */}
                    <motion.div
                      className="absolute -top-0.5 -right-0.5 w-2 h-2 bg-green-500 rounded-full"
                      animate={{
                        scale: [1, 1.5, 1],
                        opacity: [1, 0.5, 1],
                      }}
                      transition={{
                        duration: 1.5,
                        repeat: Infinity,
                        ease: 'easeInOut',
                      }}
                    />
                  </>
                ) : (
                  <WifiOff className="h-3 w-3" />
                )}
              </div>

              {/* Status text */}
              <span>
                {isConnecting
                  ? 'Connecting...'
                  : isConnected
                    ? 'Live'
                    : 'Offline'}
              </span>

              {/* Pulsing effect when connecting */}
              {isConnecting && (
                <motion.div
                  className="absolute inset-0 rounded-full border-2 border-primary/30"
                  animate={{
                    scale: [1, 1.2, 1],
                    opacity: [0.5, 0, 0.5],
                  }}
                  transition={{
                    duration: 1.5,
                    repeat: Infinity,
                    ease: 'easeInOut',
                  }}
                />
              )}
            </motion.div>
          </TooltipTrigger>
          <TooltipContent side="top">
            <p>{isConnected
              ? 'Live updates • Connected to Supabase'
              : 'Disconnected • Reconnecting...'}</p>
          </TooltipContent>
        </Tooltip>
      </div>
    </div>
  )
}

/**
 * Breaking List Component
 *
 * Markets list component with infinite scroll functionality.
 * Displays breaking markets in grid or list view mode.
 *
 * @features
 * - Grid view: 2 columns on tablet, 3 on desktop
 * - List view: full-width cards
 * - Infinite scroll with Intersection Observer
 * - Load More button (fallback)
 * - Loading skeletons at bottom
 * - Empty state: "No breaking markets found"
 * - Framer Motion stagger animation on mount
 * - Real-time updates with flash animation
 *
 * @component BreakingList
 */

'use client'

import * as React from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import Link from 'next/link'
import { BreakingMarketCard } from './breaking-market-card'
import { BreakingMarketCardSkeleton } from './breaking-market-card-skeleton'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import { ChevronDown, TrendingDown, TrendingUp } from 'lucide-react'
import { cn } from '@/lib/utils'
import type { BreakingMarket } from '@/types/breaking.types'

/**
 * BreakingList Props
 */
export interface BreakingListProps {
  /** Array of breaking markets to display */
  markets: BreakingMarket[]
  /** Current view mode */
  viewMode: 'grid' | 'list'
  /** Callback when load more is triggered */
  onLoadMore: () => void
  /** Whether there are more markets to load */
  hasMore: boolean
  /** Whether currently loading */
  isLoading: boolean
}

/**
 * Container animation variants for stagger effect
 */
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.05,
    },
  },
}

/**
 * Item animation variants
 */
const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      type: 'spring',
      stiffness: 100,
      damping: 15,
    },
  },
}

/**
 * BreakingList Component
 *
 * Displays breaking markets in grid or list layout.
 * Supports infinite scroll pagination.
 *
 * @example
 * ```tsx
 * <BreakingList
 *   markets={markets}
 *   viewMode="grid"
 *   onLoadMore={handleLoadMore}
 *   hasMore={true}
 *   isLoading={false}
 * />
 * ```
 */
export function BreakingList({
  markets,
  viewMode,
  onLoadMore,
  hasMore,
  isLoading,
}: BreakingListProps) {
  // Intersection Observer ref for infinite scroll
  const loadMoreRef = React.useRef<HTMLDivElement>(null)
  const [isIntersecting, setIsIntersecting] = React.useState(false)

  /**
   * Set up Intersection Observer for infinite scroll
   */
  React.useEffect(() => {
    const ref = loadMoreRef.current
    if (!ref) return

    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && hasMore && !isLoading) {
          setIsIntersecting(true)
        }
      },
      { threshold: 0.1 }
    )

    observer.observe(ref)

    return () => {
      observer.disconnect()
    }
  }, [hasMore, isLoading])

  /**
   * Trigger load more when intersecting
   */
  React.useEffect(() => {
    if (isIntersecting && hasMore && !isLoading) {
      onLoadMore()
      setIsIntersecting(false)
    }
  }, [isIntersecting, hasMore, isLoading, onLoadMore])

  /**
   * Format currency for display
   */
  const formatCurrency = React.useCallback((value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(value)
  }, [])

  /**
   * Render list view card
   */
  const renderListCard = React.useCallback(
    (market: BreakingMarket, index: number) => {
      const isUp = market.trend === 'up'
      const priceChangePercent = (market.price_change_24h * 100).toFixed(2)

      return (
        <Link key={market.id} href={`/markets/${market.id}`} className="block">
          <motion.div variants={itemVariants}>
            <Card
              variant="glass"
              className="group hover:shadow-lg hover:border-primary/30 transition-all duration-200 cursor-pointer"
            >
              <CardContent className="p-4">
                <div className="flex items-center gap-4">
                  {/* Rank Badge */}
                  <div className="flex items-center justify-center w-8 h-8 rounded-full bg-primary/10 text-primary font-bold text-sm shrink-0">
                    {index + 1}
                  </div>

                  {/* Market Info */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-1">
                      <h3 className="font-semibold truncate group-hover:text-primary transition-colors">
                        {market.question}
                      </h3>
                      {market.category && (
                        <span className="text-xs text-muted-foreground shrink-0">
                          {market.category}
                        </span>
                      )}
                    </div>

                    {/* Stats Row */}
                    <div className="flex items-center gap-4 text-sm">
                      {/* Current Price */}
                      <div className="flex items-center gap-1">
                        <span className="text-muted-foreground">Price:</span>
                        <span className="font-medium tabular-nums">
                          {Math.round((market.current_price || 0) * 100)}%
                        </span>
                      </div>

                      {/* Price Change */}
                      <div
                        className={cn(
                          'flex items-center gap-1',
                          isUp ? 'text-green-500' : 'text-red-500'
                        )}
                      >
                        {isUp ? (
                          <TrendingUp className="h-3 w-3" />
                        ) : (
                          <TrendingDown className="h-3 w-3" />
                        )}
                        <span className="font-medium tabular-nums">
                          {priceChangePercent}%
                        </span>
                      </div>

                      {/* Volume */}
                      <div className="flex items-center gap-1 text-muted-foreground">
                        <span>Vol:</span>
                        <span className="tabular-nums">
                          {formatCurrency(market.volume || 0)}
                        </span>
                      </div>

                      {/* Movement Score */}
                      <div className="flex items-center gap-1">
                        <span className="text-muted-foreground text-xs">Score:</span>
                        <span className="text-xs font-medium tabular-nums">
                          {(market.movement_score * 100).toFixed(0)}%
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Trend Icon */}
                  <div
                    className={cn(
                      'flex items-center justify-center w-10 h-10 rounded-full',
                      isUp
                        ? 'bg-green-500/10 text-green-500'
                        : 'bg-red-500/10 text-red-500'
                    )}
                  >
                    {isUp ? (
                      <TrendingUp className="h-5 w-5" />
                    ) : (
                      <TrendingDown className="h-5 w-5" />
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        </Link>
      )
    },
    [formatCurrency]
  )

  if (markets.length === 0) {
    return (
      <Card variant="glass">
        <CardContent className="p-12 text-center">
          <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-muted mb-4">
            <TrendingUp className="h-8 w-8 text-muted-foreground" />
          </div>
          <h3 className="text-lg font-semibold mb-2">No breaking markets found</h3>
          <p className="text-muted-foreground">
            Markets with significant price movements will appear here
          </p>
        </CardContent>
      </Card>
    )
  }

  return (
    <div>
      {/* Markets Grid/List */}
      <motion.div
        variants={containerVariants}
        initial="hidden"
        animate="visible"
        className={
          viewMode === 'grid'
            ? 'grid gap-4 grid-cols-1 md:grid-cols-2 lg:grid-cols-3'
            : 'space-y-3'
        }
      >
        <AnimatePresence mode="popLayout">
          {markets.map((market, index) =>
            viewMode === 'grid' ? (
              <motion.div key={market.id} variants={itemVariants} layout>
                <BreakingMarketCard market={market} rank={index + 1} />
              </motion.div>
            ) : (
              renderListCard(market, index)
            )
          )}
        </AnimatePresence>
      </motion.div>

      {/* Loading Skeletons */}
      {isLoading && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className={
            viewMode === 'grid'
              ? 'grid gap-4 grid-cols-1 md:grid-cols-2 lg:grid-cols-3 mt-4'
              : 'space-y-3 mt-4'
          }
        >
          {Array.from({ length: viewMode === 'grid' ? 6 : 3 }).map((_, i) => (
            <BreakingMarketCardSkeleton key={`skeleton-${i}`} viewMode={viewMode} />
          ))}
        </motion.div>
      )}

      {/* Load More Trigger (Infinite Scroll) */}
      <div ref={loadMoreRef} className="h-4" />

      {/* Load More Button (Fallback) */}
      {hasMore && !isLoading && (
        <div className="flex justify-center mt-6">
          <Button
            variant="outline"
            onClick={onLoadMore}
            className="gap-2"
          >
            Load More
            <ChevronDown className="h-4 w-4" />
          </Button>
        </div>
      )}

      {/* End of List Indicator */}
      {!hasMore && markets.length > 0 && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="text-center mt-6 text-sm text-muted-foreground"
        >
          Showing all {markets.length} breaking markets
        </motion.div>
      )}
    </div>
  )
}

/**
 * Breaking Market Card Component
 *
 * Main card component for displaying breaking markets with significant
 * price movements. Features glassmorphism design, real-time updates,
 * and Apple-inspired animations.
 *
 * @features
 * - Glassmorphism card with backdrop blur
 * - Rank badge (top-left)
 * - Market image thumbnail
 * - Question and category tags
 * - Current price → Previous price display
 * - Mini sparkline chart (24h history)
 * - Volume change indicator
 * - Movement trend arrow
 * - Hover effects (scale + shadow)
 * - Flash animation on price updates
 * - Responsive layout
 * - Accessibility (keyboard nav, ARIA labels)
 */

'use client'

import * as React from 'react'
import Link from 'next/link'
import { motion, AnimatePresence } from 'framer-motion'
import { TrendingUp, TrendingDown } from 'lucide-react'
import { cn } from '@/lib/utils'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import type { BreakingMarket } from '@/types/breaking.types'
import { BreakingRankBadge } from './breaking-rank-badge'
import { MovementIndicator } from './movement-indicator'
import { MiniSparkline } from './mini-sparkline'

/**
 * BreakingMarketCard Props
 */
export interface BreakingMarketCardProps {
  /** Breaking market data to display */
  market: BreakingMarket
  /** Rank in breaking markets list */
  rank: number
  /** Click handler for navigation/selection */
  onClick?: () => void
  /** Show both current and previous prices */
  showPreviousPrice?: boolean
  /** Additional CSS class names */
  className?: string
  /** Callback invoked when market receives real-time update (triggers flash animation) */
  onUpdate?: (marketId: string) => void
}

/**
 * BreakingMarketCard Component
 *
 * Displays a breaking market with:
 * - Rank badge (gold/silver/bronze)
 * - Market image
 * - Question text
 * - Category tags
 * - Current price → Previous price
 * - Trend arrow indicator
 * - Mini sparkline chart
 * - Volume change
 *
 * @example
 * ```tsx
 * <BreakingMarketCard
 *   market={breakingMarket}
 *   rank={1}
 *   showPreviousPrice
 *   onClick={() => router.push(`/markets/${market.id}`)}
 * />
 * ```
 */
export const BreakingMarketCard = React.memo<BreakingMarketCardProps>(({
  market,
  rank,
  onClick,
  showPreviousPrice = false,
  className,
  onUpdate
}) => {
  const [isHovered, setIsHovered] = React.useState(false)
  const [priceFlash, setPriceFlash] = React.useState<'up' | 'down' | null>(null)
  const previousMarketRef = React.useRef<BreakingMarket | null>(null)

  /**
   * Flash animation when price changes significantly
   * Detects both initial load (from price_change_24h) and real-time updates
   */
  React.useEffect(() => {
    const prevMarket = previousMarketRef.current

    // Check for significant price change on initial load
    if (!prevMarket && market.price_change_24h) {
      if (market.price_change_24h > 0.05) {
        setPriceFlash('up')
        const timer = setTimeout(() => setPriceFlash(null), 1000)
        return () => clearTimeout(timer)
      } else if (market.price_change_24h < -0.05) {
        setPriceFlash('down')
        const timer = setTimeout(() => setPriceFlash(null), 1000)
        return () => clearTimeout(timer)
      }
    }

    // Check for real-time price updates
    if (prevMarket && prevMarket.current_price !== market.current_price) {
      const currentPrice = market.current_price ?? 0
      const previousPrice = prevMarket.current_price ?? 0
      const priceChange = Math.abs((currentPrice - previousPrice) / (previousPrice || 1))

      if (priceChange > 0.05) {
        // Flash animation based on price direction
        const direction = currentPrice > previousPrice ? 'up' : 'down'
        setPriceFlash(direction)

        // Trigger animation sequence: pulse -> highlight -> normal
        const pulseTimer = setTimeout(() => setPriceFlash(null), 300)
        const highlightTimer = setTimeout(() => setPriceFlash(direction), 300)
        const normalTimer = setTimeout(() => setPriceFlash(null), 1000)

        // Call onUpdate callback to notify parent
        if (onUpdate) {
          onUpdate(market.id)
        }

        return () => {
          clearTimeout(pulseTimer)
          clearTimeout(highlightTimer)
          clearTimeout(normalTimer)
        }
      }
    }

    // Update ref for next comparison
    previousMarketRef.current = market
  }, [market.current_price, market.price_change_24h, market.id, onUpdate])

  /**
   * Handle card click
   */
  const handleClick = React.useCallback(() => {
    if (onClick) {
      onClick()
    }
  }, [onClick])

  /**
   * Calculate previous price from 24h change
   */
  const previousPrice = React.useMemo(() => {
    const change = market.price_change_24h
    if (market.current_price) {
      return Math.max(0, Math.min(1, market.current_price / (1 + change)))
    }
    return 0.5
  }, [market.current_price, market.price_change_24h])

  /**
   * Format price as percentage
   */
  const formatPrice = (price: number) => {
    return `${Math.round(price * 100)}%`
  }

  /**
   * Get volume change color
   */
  const volumeChangeColor = React.useMemo(() => {
    if (market.volume_change_24h > 0) return 'text-green-500'
    if (market.volume_change_24h < 0) return 'text-red-500'
    return 'text-gray-400'
  }, [market.volume_change_24h])

  /**
   * Generate placeholder image URL
   */
  const imageUrl = React.useMemo(() => {
    if (market.image_url) return market.image_url

    // Generate deterministic gradient based on market ID
    const hash = market.id.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)
    const hue1 = hash % 360
    const hue2 = (hue1 + 40) % 360
    return `data:image/svg+xml;base64,${Buffer.from(
      `<svg width="80" height="80" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="grad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:hsl(${hue1},70%,80%)" />
            <stop offset="100%" style="stop-color:hsl(${hue2},70%,80%)" />
          </linearGradient>
        </defs>
        <rect width="100%" height="100%" fill="url(#grad)" rx="8" />
      </svg>`
    ).toString('base64')}`
  }, [market.id, market.image_url])

  /**
   * Extract price history for sparkline
   */
  const sparklineData = React.useMemo(() => {
    return market.price_history_24h.map((point) => point.price_yes)
  }, [market.price_history_24h])

  const cardContent = (
    <motion.div
      whileHover={{ scale: 1.02, y: -4 }}
      whileTap={{ scale: 0.98 }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      className="h-full"
    >
      <Card
        variant={isHovered ? 'elevated' : 'glass'}
        className={cn(
          'relative h-full overflow-hidden',
          'transition-all duration-200',
          'hover:border-primary/50 hover:shadow-xl',
          'group cursor-pointer',
          className
        )}
        onClick={handleClick}
      >
        {/* Rank Badge */}
        <BreakingRankBadge rank={rank} size="md" variant="absolute" />

        {/* Main Content */}
        <div className="relative p-4 pl-14">
          {/* Market Image + Question */}
          <div className="flex items-start gap-3 mb-3">
            {/* Thumbnail Image */}
            <div className="relative shrink-0">
              <div
                className={cn(
                  'w-16 h-16 rounded-lg overflow-hidden',
                  'bg-gradient-to-br from-primary/10 to-primary/5',
                  'border border-border/50',
                  'transition-transform duration-200',
                  'group-hover:scale-105'
                )}
              >
                <img
                  src={imageUrl}
                  alt={market.question}
                  className="w-full h-full object-cover"
                  loading="lazy"
                />
              </div>
            </div>

            {/* Question + Category */}
            <div className="flex-1 min-w-0">
              {/* Category Badge */}
              {market.category && (
                <Badge
                  variant="glass"
                  className="mb-2 text-xs font-normal"
                >
                  {market.category}
                </Badge>
              )}

              {/* Question Text */}
              <h3
                className={cn(
                  'font-semibold text-sm leading-tight',
                  'line-clamp-2',
                  'mb-1',
                  'group-hover:text-primary transition-colors duration-200'
                )}
              >
                {market.question}
              </h3>

              {/* Volume Change */}
              {market.volume_change_24h !== 0 && (
                <div
                  className={cn(
                    'flex items-center gap-1 text-xs font-medium',
                    volumeChangeColor
                  )}
                >
                  {market.volume_change_24h > 0 ? (
                    <TrendingUp className="w-3 h-3" />
                  ) : (
                    <TrendingDown className="w-3 h-3" />
                  )}
                  <span className="tabular-nums">
                    {Math.abs(market.volume_change_24h * 100).toFixed(1)}% vol
                  </span>
                </div>
              )}
            </div>
          </div>

          {/* Price Section */}
          <div className="flex items-center justify-between gap-3">
            {/* Current Price + Previous Price */}
            <div className="flex items-center gap-2">
              <AnimatePresence mode="wait">
                <motion.div
                  key={`${market.current_price}-${priceFlash}`}
                  initial={priceFlash ? { scale: 1.2, opacity: 0.8 } : false}
                  animate={{ scale: 1, opacity: 1 }}
                  exit={{ scale: 0.9, opacity: 0 }}
                  transition={{ duration: 0.3 }}
                  className="flex items-baseline gap-2"
                >
                  {/* Current Price (LARGE) */}
                  <span
                    className={cn(
                      'text-2xl font-bold tabular-nums',
                      'bg-gradient-to-r bg-clip-text text-transparent',
                      market.trend === 'up' && 'from-green-500 to-emerald-500',
                      market.trend === 'down' && 'from-red-500 to-rose-500',
                      market.trend === 'neutral' && 'from-gray-600 to-gray-700',
                      priceFlash === 'up' && 'animate-pulse',
                      priceFlash === 'down' && 'animate-pulse'
                    )}
                  >
                    {formatPrice(market.current_price || 0)}
                  </span>

                  {/* Arrow + Previous Price */}
                  {showPreviousPrice && (
                    <div className="flex items-center gap-1">
                      <span className="text-gray-400 text-sm">→</span>
                      <span className="text-gray-400 text-sm font-medium tabular-nums">
                        {formatPrice(previousPrice)}
                      </span>
                    </div>
                  )}
                </motion.div>
              </AnimatePresence>
            </div>

            {/* Movement Indicator */}
            <MovementIndicator
              currentPrice={market.current_price || 0}
              previousPrice={previousPrice}
              size={20}
            />
          </div>

          {/* Mini Sparkline Chart */}
          {sparklineData.length > 1 && (
            <div className="mt-3 h-10 w-full">
              <MiniSparkline
                data={sparklineData}
                height={40}
                color={
                  market.trend === 'up'
                    ? 'hsl(142, 76%, 36%)'
                    : market.trend === 'down'
                    ? 'hsl(0, 84%, 60%)'
                    : undefined
                }
              />
            </div>
          )}
        </div>

        {/* Shine effect on hover */}
        <motion.div
          className={cn(
            'absolute inset-0 pointer-events-none',
            'bg-gradient-to-tr from-white/0 via-white/5 to-white/0',
            'opacity-0 group-hover:opacity-100',
            'transition-opacity duration-300'
          )}
          animate={isHovered ? { x: ['0%', '100%'] } : {}}
          transition={{ duration: 0.6, ease: 'linear' }}
        />
      </Card>
    </motion.div>
  )

  // Wrap in Link if onClick is not provided (default navigation)
  if (!onClick) {
    return (
      <Link
        href={`/markets/${market.id}`}
        className="block w-full md:w-[400px]"
        style={{ width: '100%' }}
      >
        {cardContent}
      </Link>
    )
  }

  return <div className="w-full md:w-[400px]">{cardContent}</div>
})

