// Current: Generic error handling
async fetchMarkets(query: GammaMarketsQuery = {}): Promise<GammaMarket[]> {
  // ...
  const response = await this.fetchWithTimeout(url)

  if (!response.ok) {
    throw this.createError(response)  // Good: Creates error
  }

  const data: GammaMarket[] = await response.json()
  return data
}

// Issue: createError may not provide enough context
private createError(response: Response): Error {
  return new Error(`HTTP ${response.status}: ${response.statusText}`)
  // Lacks: Request URL, query params, retry info
}

// BETTER: Add context
private createError(response: Response, url: string, query: GammaMarketsQuery): Error {
  const error = new Error(
    `Gamma API request failed: ${response.status} ${response.statusText}\n` +
    `URL: ${url}\n` +
    `Query: ${JSON.stringify(query)}`
  )

  // Add retry info
  ;(error as any).status = response.status
  ;(error as any).retryable = response.status >= 500

  return error
}
