const previousPrice = React.useMemo(() => {
  const change = market.price_change_24h
  if (market.current_price) {
    return Math.max(0, Math.min(1, market.current_price / (1 + change)))
    // ISSUE: If change is -1, division by zero
  }
  return 0.5
}, [market.current_price, market.price_change_24h])

// BETTER:
const previousPrice = React.useMemo(() => {
  const change = market.price_change_24h ?? 0
  const currentPrice = market.current_price ?? 0.5
  const divisor = 1 + change

  // Guard against division by zero
  if (Math.abs(divisor) < 0.001) {
    return 0.5
  }

  return Math.max(0, Math.min(1, currentPrice / divisor))
}, [market.current_price, market.price_change_24h])
