// PROBLEMATIC: useMemo called conditionally
if (!data || data.length === 0) {
  return <EmptyState />
}

// This violates React Hooks rules
const transformedData = React.useMemo(() => {
  return data.map((value, index) => ({ value, index }))
}, [data])

// BETTER: Always call hooks, return early after
