```typescript
// Current: One giant function (complexity 15)
export const selectFilteredMarkets = (state): Market[] => {
  let filtered = [...state.markets]

  // 12 different filter operations...
  // Each adding to complexity

  return filtered
}

// BETTER: Split into smaller functions
const filterByActive = (markets: Market[], active?: boolean): Market[] => {
  if (active === undefined) return markets
  return markets.filter(m => m.active === active)
}

const filterByCategories = (markets: Market[], categories: string[]): Market[] => {
  if (!categories.length) return markets
  return markets.filter(m => categories.includes(m.category ?? ''))
}

// Compose filters
export const selectFilteredMarkets = (state): Market[] => {
  return pipe(
    state.markets,
    (markets) => filterByActive(markets, state.filters.active),
    (markets) => filterByCategories(markets, state.filters.categories),
    // ... other filters
  )
}
```

