toolUseResult.originalFile:
/**
 * Market Store
 *
 * Zustand store for market state management including filters,
 * sorting, view modes, and market data caching.
 *
 * @features
 * - Market list with real-time updates
 * - Advanced filtering (category, tags, price, liquidity)
 * - Multiple sort options
 * - View mode switching (grid/list/compact)
 * - Search functionality
 * - Optimized selectors for derived state
 *
 * @example
 * ```ts
 * const { markets, filters, setFilters, setSearch } = useMarketStore()
 * const filteredMarkets = useMarketStore(selectFilteredMarkets)
 * ```
 */

import { create } from 'zustand'
import { devtools, persist } from 'zustand/middleware'
import { immer } from 'zustand/middleware/immer'
import type {
  Market,
  MarketFilterOptions,
  MarketSortOption,
  MarketViewMode
} from '@/types/market.types'
import {
  MarketSortField,
  MarketSortDirection
} from '@/types/market.types'

// ============================================================================
// STATE INTERFACE
// ============================================================================

/**
 * Market Store State
 *
 * Complete state for market management
 */
export interface MarketState {
  // Data
  /** All loaded markets */
  markets: Market[]
  /** Currently selected market */
  selectedMarket: Market | null
  /** Favorite market IDs */
  favoriteMarkets: Set<string>

  // Filters
  /** Active filter options */
  filters: MarketFilterOptions
  /** Current sort option */
  sortOption: MarketSortOption
  /** Search query string */
  searchQuery: string
  /** Current view mode */
  viewMode: MarketViewMode

  // UI State
  /** Loading state */
  loading: boolean
  /** Error message */
  error: string | null
  /** Last data refresh timestamp */
  lastRefresh: number | null

  // Pagination
  /** Current page (1-indexed) */
  page: number
  /** Items per page */
  pageSize: number
  /** Total filtered items count */
  totalFiltered: number
}

// ============================================================================
// INITIAL STATE
// ============================================================================

/**
 * Default filter options
 */
const defaultFilters: MarketFilterOptions = {
  active: true,
  categories: [],
  tags: [],
  minLiquidity: undefined,
  maxLiquidity: undefined,
  minVolume: undefined,
  maxVolume: undefined,
  priceRange: undefined,
  closingSoon: false,
  hot: false,
  status: undefined,
  searchQuery: undefined
}

/**
 * Default sort option
 */
const defaultSortOption: MarketSortOption = {
  field: MarketSortField.END_DATE,
  direction: MarketSortDirection.ASC
}

/**
 * Initial market store state
 */
const initialState: MarketState = {
  markets: [],
  selectedMarket: null,
  favoriteMarkets: new Set<string>(),

  filters: defaultFilters,
  sortOption: defaultSortOption,
  searchQuery: '',
  viewMode: MarketViewMode.GRID,

  loading: false,
  error: null,
  lastRefresh: null,

  page: 1,
  pageSize: 20,
  totalFiltered: 0
}

// ============================================================================
// ACTIONS
// ============================================================================

/**
 * Market Store Actions
 *
 * All actions available in the market store
 */
export interface MarketActions {
  // Data Actions

  /**
   * Set all markets
   * @param markets - Array of markets to set
   */
  setMarkets: (markets: Market[]) => void

  /**
   * Add or update a single market
   * @param market - Market to add or update
   */
  upsertMarket: (market: Market) => void

  /**
   * Remove a market by ID
   * @param marketId - Market ID to remove
   */
  removeMarket: (marketId: string) => void

  /**
   * Set selected market
   * @param market - Market to select or null to deselect
   */
  setSelectedMarket: (market: Market | null) => void

  /**
   * Toggle favorite status for a market
   * @param marketId - Market ID to toggle
   */
  toggleFavorite: (marketId: string) => void

  /**
   * Check if market is favorited
   * @param marketId - Market ID to check
   * @returns Whether market is favorited
   */
  isFavorite: (marketId: string) => boolean

  // Filter Actions

  /**
   * Set filter options
   * @param filters - New filter options (partial)
   */
  setFilters: (filters: Partial<MarketFilterOptions>) => void

  /**
   * Reset all filters to defaults
   */
  resetFilters: () => void

  /**
   * Set search query
   * @param query - Search query string
   */
  setSearch: (query: string) => void

  // Sort Actions

  /**
   * Set sort option
   * @param field - Sort field
   * @param direction - Sort direction
   */
  setSort: (field: MarketSortField, direction?: MarketSortDirection) => void

  /**
   * Toggle sort direction for current field
   */
  toggleSortDirection: () => void

  // View Mode Actions

  /**
   * Set view mode
   * @param mode - View mode to set
   */
  setViewMode: (mode: MarketViewMode) => void

  /**
   * Toggle between grid and list view
   */
  toggleViewMode: () => void

  // Pagination Actions

  /**
   * Set current page
   * @param page - Page number (1-indexed)
   */
  setPage: (page: number) => void

  /**
   * Set page size
   * @param size - Items per page
   */
  setPageSize: (size: number) => void

  /**
   * Go to next page
   */
  nextPage: () => void

  /**
   * Go to previous page
   */
  prevPage: () => void

  // Loading State Actions

  /**
   * Set loading state
   * @param loading - Loading status
   */
  setLoading: (loading: boolean) => void

  /**
   * Set error state
   * @param error - Error message or null
   */
  setError: (error: string | null) => void

  /**
   * Reset state to initial values
   */
  reset: () => void

  /**
   * Refresh markets (update last refresh timestamp)
   */
  refresh: () => void
}

// ============================================================================
// STORE CREATION
// ============================================================================

/**
 * Market Store
 *
 * Main market state management store with:
 * - DevTools integration for debugging
 * - Persistence for filters and view mode
 * - Immer middleware for immutable updates
 */
export const useMarketStore = create<MarketState & MarketActions>()(
  devtools(
    persist(
      immer((set, get) => ({
        ...initialState,

        // Data Actions
        setMarkets: (markets) =>
          set((state) => {
            state.markets = markets
            state.lastRefresh = Date.now()
          }),

        upsertMarket: (market) =>
          set((state) => {
            const index = state.markets.findIndex((m) => m.id === market.id)
            if (index >= 0) {
              state.markets[index] = market
            } else {
              state.markets.push(market)
            }
          }),

        removeMarket: (marketId) =>
          set((state) => {
            state.markets = state.markets.filter((m) => m.id !== marketId)
            if (state.selectedMarket?.id === marketId) {
              state.selectedMarket = null
            }
          }),

        setSelectedMarket: (market) =>
          set((state) => {
            state.selectedMarket = market
          }),

        toggleFavorite: (marketId) =>
          set((state) => {
            if (state.favoriteMarkets.has(marketId)) {
              state.favoriteMarkets.delete(marketId)
            } else {
              state.favoriteMarkets.add(marketId)
            }
          }),

        isFavorite: (marketId) => {
          return get().favoriteMarkets.has(marketId)
        },

        // Filter Actions
        setFilters: (filters) =>
          set((state) => {
            state.filters = { ...state.filters, ...filters }
            state.page = 1 // Reset to first page when filters change
          }),

        resetFilters: () =>
          set((state) => {
            state.filters = defaultFilters
            state.searchQuery = ''
            state.page = 1
          }),

        setSearch: (query) =>
          set((state) => {
            state.searchQuery = query
            state.page = 1 // Reset to first page when search changes
          }),

        // Sort Actions
        setSort: (field, direction) =>
          set((state) => {
            // If same field, toggle direction if direction not provided
            if (state.sortOption.field === field && !direction) {
              state.sortOption.direction =
                state.sortOption.direction === MarketSortDirection.ASC
                  ? MarketSortDirection.DESC
                  : MarketSortDirection.ASC
            } else {
              state.sortOption.field = field
              state.sortOption.direction = direction || MarketSortDirection.ASC
            }
          }),

        toggleSortDirection: () =>
          set((state) => {
            state.sortOption.direction =
              state.sortOption.direction === MarketSortDirection.ASC
                ? MarketSortDirection.DESC
                : MarketSortDirection.ASC
          }),

        // View Mode Actions
        setViewMode: (mode) =>
          set((state) => {
            state.viewMode = mode
          }),

        toggleViewMode: () =>
          set((state) => {
            state.viewMode =
              state.viewMode === MarketViewMode.GRID
                ? MarketViewMode.LIST
                : MarketViewMode.GRID
          }),

        // Pagination Actions
        setPage: (page) =>
          set((state) => {
            state.page = Math.max(1, page)
          }),

        setPageSize: (size) =>
          set((state) => {
            state.pageSize = size
            state.page = 1 // Reset to first page when page size changes
          }),

        nextPage: () =>
          set((state) => {
            const maxPage = Math.ceil(state.totalFiltered / state.pageSize)
            state.page = Math.min(state.page + 1, maxPage)
          }),

        prevPage: () =>
          set((state) => {
            state.page = Math.max(1, state.page - 1)
          }),

        // Loading State Actions
        setLoading: (loading) =>
          set((state) => {
            state.loading = loading
          }),

        setError: (error) =>
          set((state) => {
            state.error = error
            state.loading = false
          }),

        reset: () =>
          set((state) => {
            Object.assign(state, initialState)
            state.favoriteMarkets = new Set<string>()
          }),

        refresh: () =>
          set((state) => {
            state.lastRefresh = Date.now()
          })
      })),
      {
        name: 'market-store',
        // Only persist specific fields (not the markets data itself)
        partialize: (state) => ({
          filters: state.filters,
          sortOption: state.sortOption,
          searchQuery: state.searchQuery,
          viewMode: state.viewMode,
          favoriteMarkets: Array.from(state.favoriteMarkets),
          page: state.page,
          pageSize: state.pageSize
        }),
        // Rehydrate Set from array
        onRehydrateStorage: () => (state) => {
          if (state) {
            state.favoriteMarkets = new Set(state.favoriteMarkets as unknown as Set<string>)
          }
        }
      }
    ),
    { name: 'MarketStore' }
  )
)

// ============================================================================
// SELECTORS (Derived State)
// ============================================================================

/**
 * Select filtered and sorted markets
 * @returns Filtered and sorted markets for current page
 */
export const selectFilteredMarkets = (state: MarketState & MarketActions): Market[] => {
  let filtered = [...state.markets]

  // Apply filters
  const { filters, searchQuery } = state

  if (filters.active !== undefined) {
    filtered = filtered.filter((m) => m.active === filters.active)
  }

  if (filters.categories && filters.categories.length > 0) {
    filtered = filtered.filter((m) => filters.categories!.includes(m.category || ''))
  }

  if (filters.tags && filters.tags.length > 0) {
    filtered = filtered.filter((m) =>
      m.tags.some((tag) => filters.tags!.includes(tag.slug))
    )
  }

  if (filters.minLiquidity !== undefined) {
    filtered = filtered.filter((m) => (m.liquidity || 0) >= filters.minLiquidity!)
  }

  if (filters.maxLiquidity !== undefined) {
    filtered = filtered.filter((m) => (m.liquidity || 0) <= filters.maxLiquidity!)
  }

  if (filters.minVolume !== undefined) {
    filtered = filtered.filter((m) => (m.volume || 0) >= filters.minVolume!)
  }

  if (filters.maxVolume !== undefined) {
    filtered = filtered.filter((m) => (m.volume || 0) <= filters.maxVolume!)
  }

  if (filters.priceRange) {
    const [min, max] = filters.priceRange
    filtered = filtered.filter((m) => {
      const price = m.current_price || 0
      return price >= min && price <= max
    })
  }

  if (filters.closingSoon) {
    const oneDay = 24 * 60 * 60 * 1000
    const tomorrow = Date.now() + oneDay
    filtered = filtered.filter((m) => {
      const endDate = m.end_date ? new Date(m.end_date).getTime() : Infinity
      return endDate <= tomorrow
    })
  }

  if (filters.hot) {
    // Sort by volume and take top 20%
    const sortedByVolume = [...filtered].sort((a, b) => (b.volume || 0) - (a.volume || 0))
    const hotThreshold = Math.floor(sortedByVolume.length * 0.2)
    const hotIds = new Set(sortedByVolume.slice(0, hotThreshold).map((m) => m.id))
    filtered = filtered.filter((m) => hotIds.has(m.id))
  }

  // Apply search query
  if (searchQuery.trim()) {
    const query = searchQuery.toLowerCase()
    filtered = filtered.filter(
      (m) =>
        m.question.toLowerCase().includes(query) ||
        m.description?.toLowerCase().includes(query) ||
        m.tags.some((tag) => tag.label.toLowerCase().includes(query))
    )
  }

  // Apply sorting
  const { field, direction } = state.sortOption
  const multiplier = direction === MarketSortDirection.ASC ? 1 : -1

  filtered.sort((a, b) => {
    let comparison = 0

    switch (field) {
      case MarketSortField.END_DATE:
        const aDate = a.end_date ? new Date(a.end_date).getTime() : Infinity
        const bDate = b.end_date ? new Date(b.end_date).getTime() : Infinity
        comparison = aDate - bDate
        break
      case MarketSortField.VOLUME:
        comparison = (a.volume || 0) - (b.volume || 0)
        break
      case MarketSortField.LIQUIDITY:
        comparison = (a.liquidity || 0) - (b.liquidity || 0)
        break
      case MarketSortField.PRICE:
        comparison = (a.current_price || 0) - (b.current_price || 0)
        break
      case MarketSortField.CREATED:
        comparison = new Date(a.created_at).getTime() - new Date(b.created_at).getTime()
        break
      case MarketSortField.PRICE_CHANGE_24H:
        comparison = (a.price_change_24h || 0) - (b.price_change_24h || 0)
        break
      default:
        comparison = 0
    }

    return comparison * multiplier
  })

  return filtered
}

/**
 * Select paginated markets
 * @returns Markets for current page
 */
export const selectPaginatedMarkets = (state: MarketState & MarketActions): Market[] => {
  const filtered = selectFilteredMarkets(state)
  const { page, pageSize } = state

  const start = (page - 1) * pageSize
  const end = start + pageSize

  return filtered.slice(start, end)
}

/**
 * Select favorite markets
 * @returns Array of favorite markets
 */
export const selectFavoriteMarkets = (state: MarketState & MarketActions): Market[] => {
  return state.markets.filter((m) => state.favoriteMarkets.has(m.id))
}

/**
 * Select market statistics
 * @returns Statistics about current market state
 */
export const selectMarketStats = (state: MarketState & MarketActions) => {
  const filtered = selectFilteredMarkets(state)
  const totalPages = Math.ceil(filtered.length / state.pageSize)

  return {
    totalMarkets: state.markets.length,
    filteredMarkets: filtered.length,
    favoriteCount: state.favoriteMarkets.size,
    currentPage: state.page,
    totalPages,
    hasNextPage: state.page < totalPages,
    hasPrevPage: state.page > 1
  }
}

/**
 * Market List Component
 *
 * List/table view alternative to grid for data-dense display.
 * Features sortable columns, expandable rows, and sticky header.
 *
 * @features
 * - Horizontal layout (market info | price | actions)
 * - Compact mode for mobile
 * - Sortable columns (click header to sort)
 * - Expandable rows for details
 * - Sticky header with column names
 * - Better for data-dense display
 * - Integrates with useMarkets hook
 */

'use client'

import * as React from 'react'
import Link from 'next/link'
import { motion, AnimatePresence } from 'framer-motion'
import {
  ChevronDown,
  ChevronUp,
  ChevronRight,
  TrendingUp,
  TrendingDown,
  Heart
} from 'lucide-react'
import { cn } from '@/lib/utils'
import { Badge } from '@/components/ui/badge'
import { Button } from '@/components/ui/button'
import type { Market, MarketSortField, MarketSortDirection } from '@/types/market.types'
import { useMarketStore } from '@/stores/market.store'
import { MarketCardMetaInline } from './market-card-meta'

/**
 * Market List Props
 */
export interface MarketListProps {
  /** Array of markets to display */
  markets: Market[]
  /** Loading state */
  loading?: boolean
  /** Error state */
  error?: string | null
  /** Initial sort field */
  sortField?: MarketSortField
  /** Initial sort direction */
  sortDirection?: MarketSortDirection
  /** Sort change callback */
  onSortChange?: (field: MarketSortField, direction: MarketSortDirection) => void
  /** Enable compact mode */
  compact?: boolean
  /** Enable row expansion */
  expandable?: boolean
  /** Show favorite column */
  showFavorite?: boolean
  /** Additional CSS class names */
  className?: string
}

/**
 * Column definition
 */
interface Column {
  id: string
  label: string
  sortable: boolean
  className?: string
}

/**
 * Sort indicator component
 */
function SortIndicator({
  direction,
  active
}: {
  direction?: MarketSortDirection
  active?: boolean
}) {
  if (!active) return null

  return (
    <motion.span
      initial={{ opacity: 0, y: -5 }}
      animate={{ opacity: 1, y: 0 }}
      className="inline-flex ml-1"
    >
      {direction === 'asc' ? (
        <ChevronUp className="h-4 w-4" />
      ) : (
        <ChevronDown className="h-4 w-4" />
      )}
    </motion.span>
  )
}

/**
 * Market List Row Component
 *
 * Individual row in the market list.
 */
function MarketListRow({
  market,
  compact,
  expanded,
  onToggleExpand,
  showFavorite
}: {
  market: Market
  compact?: boolean
  expanded: boolean
  onToggleExpand: () => void
  showFavorite?: boolean
}) {
  const { isFavorite, toggleFavorite } = useMarketStore()
  const favorite = isFavorite(market.id)

  const handleFavoriteClick = (e: React.MouseEvent) => {
    e.preventDefault()
    e.stopPropagation()
    toggleFavorite(market.id)
  }

  const priceChange = market.price_change_24h ?? 0
  const isPositive = priceChange > 0
  const isNegative = priceChange < 0

  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, height: 0 }}
      className="border-b border-border last:border-b-0"
    >
      <Link href={`/markets/${market.id}`} className="block">
        <div
          className={cn(
            'grid gap-4 px-4 py-3 transition-colors hover:bg-accent/50',
            compact
              ? 'grid-cols-[1fr_auto_auto] items-center'
              : 'grid-cols-[1fr_auto_auto_auto] items-center'
          )}
        >
          {/* Market Info */}
          <div className="min-w-0">
            <div className="flex items-start gap-3">
              {/* Expand Button (if expandable) */}
              {!compact && (
                <Button
                  variant="ghost"
                  size="icon"
                  className="shrink-0 h-6 w-6 mt-0.5"
                  onClick={(e) => {
                    e.preventDefault()
                    onToggleExpand()
                  }}
                  aria-label={expanded ? 'Collapse' : 'Expand'}
                >
                  <motion.div
                    animate={{ rotate: expanded ? 90 : 0 }}
                    transition={{ duration: 0.2 }}
                  >
                    <ChevronRight className="h-4 w-4" />
                  </motion.div>
                </Button>
              )}

              {/* Question and Tags */}
              <div className="flex-1 min-w-0">
                <h3 className="font-semibold text-sm line-clamp-1 mb-1 group-hover:text-primary transition-colors">
                  {market.question}
                </h3>
                <div className="flex items-center gap-2 flex-wrap">
                  {market.active ? (
                    <Badge variant="success" className="text-xs">Active</Badge>
                  ) : market.closed ? (
                    <Badge variant="secondary" className="text-xs">Closed</Badge>
                  ) : (
                    <Badge variant="outline" className="text-xs">Pending</Badge>
                  )}
                  {market.tags.slice(0, 2).map((tag) => (
                    <Badge key={tag.id} variant="outline" className="text-xs font-normal">
                      {tag.label}
                    </Badge>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Price */}
          <div className="shrink-0">
            <div className="text-right">
              <div className="font-bold text-lg tabular-nums">
                {((market.current_price ?? 0.5) * 100).toFixed(1)}%
              </div>
              <div
                className={cn(
                  'text-xs flex items-center justify-end gap-1',
                  isPositive ? 'text-success' : isNegative ? 'text-destructive' : 'text-muted-foreground'
                )}
              >
                {isPositive && <TrendingUp className="h-3 w-3" />}
                {isNegative && <TrendingDown className="h-3 w-3" />}
                <span className="tabular-nums">
                  {isPositive && '+'}
                  {priceChange.toFixed(1)}%
                </span>
              </div>
            </div>
          </div>

          {/* Volume/Liquidity (hidden in compact) */}
          {!compact && (
            <div className="shrink-0 hidden sm:block">
              <MarketCardMetaInline market={market} />
            </div>
          )}

          {/* Actions */}
          <div className="shrink-0 flex items-center gap-2">
            {showFavorite && (
              <Button
                variant="ghost"
                size="icon"
                className={cn(
                  'shrink-0 h-8 w-8',
                  favorite && 'text-red-500 hover:text-red-600'
                )}
                onClick={handleFavoriteClick}
                aria-label={favorite ? 'Remove from favorites' : 'Add to favorites'}
              >
                <Heart
                  className={cn(
                    'h-4 w-4 transition-all',
                    favorite && 'fill-current'
                  )}
                />
              </Button>
            )}
          </div>
        </div>

        {/* Expanded Content */}
        <AnimatePresence>
          {expanded && !compact && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.2 }}
              className="px-4 pb-4 overflow-hidden"
            >
              <div className="pt-4 border-t border-border/50">
                {market.description && (
                  <p className="text-sm text-muted-foreground mb-3">
                    {market.description}
                  </p>
                )}
                <div className="flex items-center gap-4 text-sm">
                  <div>
                    <span className="text-muted-foreground">Volume: </span>
                    <span className="font-medium tabular-nums">
                      ${market.volume?.toLocaleString() ?? '0'}
                    </span>
                  </div>
                  <div>
                    <span className="text-muted-foreground">Liquidity: </span>
                    <span className="font-medium tabular-nums">
                      ${market.liquidity?.toLocaleString() ?? '0'}
                    </span>
                  </div>
                  {market.end_date && (
                    <div>
                      <span className="text-muted-foreground">Ends: </span>
                      <span className="font-medium">
                        {new Date(market.end_date).toLocaleDateString()}
                      </span>
                    </div>
                  )}
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </Link>
    </motion.div>
  )
}

/**
 * Market List Component
 *
 * Table-like list view with sortable columns.
 * Better for data-dense displays and quick scanning.
 *
 * @example
 * ```tsx
 * const [sortField, setSortField] = useState<MarketSortField>(MarketSortField.VOLUME)
 * const [sortDirection, setSortDirection] = useState<MarketSortDirection>(MarketSortDirection.DESC)
 *
 * <MarketList
 *   markets={markets}
 *   sortField={sortField}
 *   sortDirection={sortDirection}
 *   onSortChange={(field, direction) => {
 *     setSortField(field)
 *     setSortDirection(direction)
 *   }}
 *   expandable
 *   showFavorite
 * />
 * ```
 */
export function MarketList({
  markets,
  loading = false,
  error = null,
  sortField = MarketSortField.VOLUME,
  sortDirection = MarketSortDirection.DESC,
  onSortChange,
  compact = false,
  expandable = true,
  showFavorite = true,
  className
}: MarketListProps) {
  const [expandedRows, setExpandedRows] = React.useState<Set<string>>(new Set())

  // Handle sort change
  const handleSort = (field: MarketSortField) => {
    if (!onSortChange) return

    // If clicking same field, toggle direction
    if (field === sortField) {
      const newDirection = sortDirection === 'asc' ? 'desc' : 'asc'
      onSortChange(field, newDirection)
    } else {
      // New field, default to desc
      onSortChange(field, 'desc')
    }
  }

  // Toggle row expansion
  const toggleRow = (marketId: string) => {
    setExpandedRows((prev) => {
      const next = new Set(prev)
      if (next.has(marketId)) {
        next.delete(marketId)
      } else {
        next.add(marketId)
      }
      return next
    })
  }

  // Column definitions
  const columns: Column[] = [
    { id: 'question', label: 'Market', sortable: false },
    { id: 'price', label: 'Price', sortable: true },
    ...(!compact ? [{ id: 'volume', label: 'Volume / Liquidity', sortable: true }] as Column[] : []),
    { id: 'actions', label: '', sortable: false, className: 'w-16' }
  ]

  // Error state
  if (error) {
    return (
      <div className={cn('text-center py-16 px-4', className)}>
        <p className="text-muted-foreground">{error}</p>
      </div>
    )
  }

  // Loading state
  if (loading && markets.length === 0) {
    return (
      <div className={cn('space-y-3', className)}>
        {Array.from({ length: 5 }).map((_, i) => (
          <div key={i} className="animate-pulse bg-muted/50 rounded-lg h-20" />
        ))}
      </div>
    )
  }

  // Empty state
  if (!loading && markets.length === 0) {
    return (
      <div className={cn('text-center py-16 px-4', className)}>
        <p className="text-muted-foreground">No markets found</p>
      </div>
    )
  }

  return (
    <div className={cn('border border-border rounded-lg overflow-hidden', className)}>
      {/* Header */}
      {!compact && (
        <div className="bg-muted/50 px-4 py-3 border-b border-border">
          <div
            className={cn(
              'grid gap-4 font-semibold text-sm text-muted-foreground',
              compact
                ? 'grid-cols-[1fr_auto_auto]'
                : 'grid-cols-[1fr_auto_auto_auto]'
            )}
          >
            {columns.map((column) => (
              <div
                key={column.id}
                className={cn(
                  column.sortable && 'cursor-pointer hover:text-foreground transition-colors select-none',
                  column.className
                )}
                onClick={() => column.sortable && handleSort(column.id as MarketSortField)}
              >
                <div className="flex items-center">
                  {column.label}
                  {column.sortable && (
                    <SortIndicator
                      direction={
                        column.id === sortField ? sortDirection : undefined
                      }
                      active={column.id === sortField}
                    />
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Rows */}
      <div className="divide-y divide-border">
        <AnimatePresence mode="popLayout">
          {markets.map((market) => (
            <MarketListRow
              key={market.id}
              market={market}
              compact={compact}
              expanded={expandedRows.has(market.id)}
              onToggleExpand={() => toggleRow(market.id)}
              showFavorite={showFavorite}
            />
          ))}
        </AnimatePresence>
      </div>
    </div>
  )
}

