import type { NextConfig } from "next";

// ============================================================================
// PERFORMANCE OPTIMIZATION CONFIGURATION (T17)
// ============================================================================

/**
 * Bundle Analyzer Configuration
 *
 * Run with: npx @next/bundle-analyzer
 * or add to package.json: "analyze": "ANALYZE=true next build"
 */
const withBundleAnalyzer = require('@next/bundle-analyzer')({
  enabled: process.env.ANALYZE === 'true',
});

/**
 * Next.js Configuration with Performance Optimizations
 *
 * Optimizations included:
 * - T17.1: Code splitting and lazy loading configuration
 * - T17.4: Image optimization settings
 * - T17.6: Bundle size optimization
 *
 * @see https://nextjs.org/docs/app/api-reference/next-config-js
 */
const nextConfig: NextConfig = {
  // Core settings
  reactStrictMode: true,
  typescript: {
    // Disable type checking during build for speed
    // Run type checking separately with 'npm run type-check'
    ignoreBuildErrors: false,
  },

  // T17.1: Code Splitting Configuration
  experimental: {
    // Optimize package imports for tree-shaking
    optimizePackageImports: [
      'lucide-react',
      'framer-motion',
      'recharts',
      'date-fns',
      '@radix-ui/react-dialog',
      '@radix-ui/react-dropdown-menu',
      '@radix-ui/react-select',
      '@radix-ui/react-tabs',
    ],
  },

  // T17.6: Bundle Size Optimization
  webpack: (config, { isServer }) => {
    // Split vendor chunks for better caching
    if (!isServer) {
      config.optimization = {
        ...config.optimization,
        splitChunks: {
          chunks: 'all',
          cacheGroups: {
            // React core
            react: {
              test: /[\\/]node_modules[\\/](react|react-dom)[\\/]/,
              name: 'react',
              priority: 40,
            },
            // Recharts (heavy chart library)
            recharts: {
              test: /[\\/]node_modules[\\/](recharts|d3-array|d3-scale|d3-shape|d3-time-format)[\\/]/,
              name: 'recharts',
              priority: 30,
            },
            // Framer Motion (animation library)
            framerMotion: {
              test: /[\\/]node_modules[\\/](framer-motion)[\\/]/,
              name: 'framer-motion',
              priority: 25,
            },
            // React Query
            reactQuery: {
              test: /[\\/]node_modules[\\/]@tanstack[\\/]react-query[\\/]/,
              name: 'react-query',
              priority: 20,
            },
            // Radix UI components
            radixUi: {
              test: /[\\/]node_modules[\\/]@radix-ui[\\/]/,
              name: 'radix-ui',
              priority: 15,
            },
            // All other node_modules
            vendor: {
              test: /[\\/]node_modules[\\/]/,
              name: 'vendor',
              priority: 10,
            },
          },
        },
      };
    }

    // Reduce size of three.js by only including needed modules
    config.module = config.module || {};
    config.module.rules = config.module.rules || [];

    return config;
  },

  // T17.4: Image Optimization
  images: {
    // Enable modern image formats
    formats: ['image/avif', 'image/webp'],
    // Remote image patterns (for Gamma API images)
    remotePatterns: [
      {
        protocol: 'https',
        hostname: '**.polymarket.com',
      },
      {
        protocol: 'https',
        hostname: '**.gamma.io',
      },
    ],
    // Device sizes for responsive images
    deviceSizes: [640, 750, 828, 1080, 1200, 1920, 2048, 3840],
    // Image sizes for srcset
    imageSizes: [16, 32, 48, 64, 96, 128, 256, 384],
    // Minimum cache TTL (in seconds)
    minimumCacheTTL: 60,
  },

  // Compression for production
  compress: true,

  // Performance budgets for CI/CD
  onDemandEntries: {
    // period (in ms) where the server will keep pages in the buffer
    maxInactiveAge: 25 * 1000,
    // number of pages that should be kept simultaneously without being disposed
    pagesBufferLength: 2,
  },

  // T17.6: Modularize imports for smaller bundle
  modularizeImports: {
    'lucide-react': {
      transform: 'lucide-react/dist/esm/icons/{{kebabCase member}}',
      skipDefaultConversion: true,
    },
  },

  // Headers for caching and performance
  async headers() {
    return [
      {
        source: '/:all*(svg|jpg|jpeg|png|gif|webp|avif)',
        locale: false,
        headers: [
          {
            key: 'Cache-Control',
            value: 'public, max-age=31536000, immutable',
          },
        ],
      },
      {
        source: '/_next/static/:path*',
        headers: [
          {
            key: 'Cache-Control',
            value: 'public, max-age=31536000, immutable',
          },
        ],
      },
    ];
  },
};

export default withBundleAnalyzer(nextConfig);

