"use client";

import * as React from "react";
import Link from "next/link";
import { motion } from "framer-motion";
import { useSearchParams } from "next/navigation";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useMarketStore, MarketViewMode, MarketSortField } from "@/stores";
import {
  Grid3x3,
  List,
  LayoutList,
  TrendingUp,
  TrendingDown,
  Clock,
  DollarSign,
  Star,
  StarOff,
  Filter,
  ChevronDown,
  Search,
  Sparkles,
} from "lucide-react";

// Animation variants
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.05,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 10 },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      type: "spring",
      stiffness: 100,
      damping: 15,
    },
  },
};

const sortOptions = [
  { value: MarketSortField.END_DATE, label: "Ending Soon" },
  { value: MarketSortField.VOLUME, label: "Volume" },
  { value: MarketSortField.LIQUIDITY, label: "Liquidity" },
  { value: MarketSortField.PRICE, label: "Price" },
  { value: MarketSortField.CREATED, label: "Newest" },
];

const categories = [
  "All",
  "Politics",
  "Sports",
  "Crypto",
  "Economics",
  "Technology",
  "Entertainment",
  "Science",
  "World Events",
];

export default function MarketsPage() {
  const searchParams = useSearchParams();
  const categoryParam = searchParams.get("category");

  const {
    markets,
    filteredMarkets,
    viewMode,
    setViewMode,
    filters,
    setFilters,
    sortOption,
    setSort,
    searchQuery,
    setSearch,
    toggleFavorite,
    isFavorite,
    resetFilters,
  } = useMarketStore();

  // Set category from URL if present
  React.useEffect(() => {
    if (categoryParam && categories.includes(categoryParam)) {
      setFilters({ categories: [categoryParam] });
    }
  }, [categoryParam, setFilters]);

  const [priceRange, setPriceRange] = React.useState<[number, number]>([0, 100]);
  const [showFilters, setShowFilters] = React.useState(false);

  const displayMarkets = React.useMemo(() => {
    return filteredMarkets;
  }, [filteredMarkets]);

  // T17.2: Memoize event handlers with useCallback to prevent child re-renders
  const handleCategoryChange = React.useCallback((category: string) => {
    if (category === "All") {
      setFilters({ categories: [] });
    } else {
      setFilters({ categories: [category.toLowerCase()] });
    }
  }, [setFilters]);

  const handleSortChange = React.useCallback((value: string) => {
    setSort(value as MarketSortField);
  }, [setSort]);

  return (
    <div className="w-full space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold">Markets</h1>
          <p className="text-muted-foreground">
            {displayMarkets.length} {displayMarkets.length === 1 ? "market" : "markets"} available
          </p>
        </div>
        <div className="flex items-center gap-2">
          {/* View Mode Toggle */}
          <div className="hidden sm:flex items-center border rounded-lg p-1">
            <Button
              variant={viewMode === MarketViewMode.GRID ? "secondary" : "ghost"}
              size="sm"
              onClick={() => setViewMode(MarketViewMode.GRID)}
              className="h-8"
            >
              <Grid3x3 className="h-4 w-4" />
            </Button>
            <Button
              variant={viewMode === MarketViewMode.LIST ? "secondary" : "ghost"}
              size="sm"
              onClick={() => setViewMode(MarketViewMode.LIST)}
              className="h-8"
            >
              <List className="h-4 w-4" />
            </Button>
            <Button
              variant={viewMode === MarketViewMode.COMPACT ? "secondary" : "ghost"}
              size="sm"
              onClick={() => setViewMode(MarketViewMode.COMPACT)}
              className="h-8"
            >
              <LayoutList className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </div>

      {/* Search and Filters */}
      <Card variant="glass">
        <CardContent className="p-4">
          <div className="flex flex-col lg:flex-row gap-4">
            {/* Search */}
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search markets..."
                  value={searchQuery}
                  onChange={(e) => setSearch(e.target.value)}
                  className="pl-9"
                />
              </div>
            </div>

            {/* Sort Dropdown */}
            <Select
              value={sortOption.field}
              onValueChange={handleSortChange}
            >
              <SelectTrigger className="w-full lg:w-[180px]">
                <SelectValue placeholder="Sort by..." />
              </SelectTrigger>
              <SelectContent>
                {sortOptions.map((option) => (
                  <SelectItem key={option.value} value={option.value}>
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            {/* Filter Toggle */}
            <Button
              variant="outline"
              className="w-full lg:w-auto"
              onClick={() => setShowFilters(!showFilters)}
            >
              <Filter className="h-4 w-4 mr-2" />
              Filters
              {showFilters ? <ChevronDown className="h-4 w-4 ml-2 rotate-180" /> : <ChevronDown className="h-4 w-4 ml-2" />}
            </Button>
          </div>

          {/* Expandable Filters */}
          {showFilters && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: "auto", opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.2 }}
              className="mt-4 pt-4 border-t space-y-4"
            >
              {/* Categories */}
              <div>
                <label className="text-sm font-medium mb-2 block">Category</label>
                <div className="flex flex-wrap gap-2">
                  {categories.map((category) => (
                    <Button
                      key={category}
                      variant={filters.categories?.includes(category.toLowerCase()) ? "default" : "outline"}
                      size="sm"
                      onClick={() => handleCategoryChange(category)}
                    >
                      {category}
                    </Button>
                  ))}
                </div>
              </div>

              {/* Price Range */}
              <div>
                <label className="text-sm font-medium mb-2 block">
                  Price Range: {priceRange[0]}% - {priceRange[1]}%
                </label>
                <Slider
                  value={priceRange}
                  onValueChange={(value) => setPriceRange(value as [number, number])}
                  min={0}
                  max={100}
                  step={5}
                  className="py-4"
                />
              </div>

              {/* Quick Filters */}
              <div className="flex flex-wrap gap-2">
                <Button
                  variant={filters.closingSoon ? "default" : "outline"}
                  size="sm"
                  onClick={() => setFilters({ closingSoon: !filters.closingSoon })}
                >
                  <Clock className="h-4 w-4 mr-1" />
                  Closing Soon
                </Button>
                <Button
                  variant={filters.hot ? "default" : "outline"}
                  size="sm"
                  onClick={() => setFilters({ hot: !filters.hot })}
                >
                  <TrendingUp className="h-4 w-4 mr-1" />
                  Hot Markets
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={resetFilters}
                >
                  Reset All
                </Button>
              </div>
            </motion.div>
          )}
        </CardContent>
      </Card>

      {/* Category Tabs */}
      <Tabs defaultValue="All" className="w-full">
        <TabsList className="w-full justify-start overflow-x-auto">
          {categories.map((category) => (
            <TabsTrigger
              key={category}
              value={category}
              onClick={() => handleCategoryChange(category)}
              className="whitespace-nowrap"
            >
              {category}
            </TabsTrigger>
          ))}
        </TabsList>
      </Tabs>

      {/* Markets Grid/List */}
      {displayMarkets.length > 0 ? (
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className={
            viewMode === MarketViewMode.GRID
              ? "grid gap-4 sm:grid-cols-2 lg:grid-cols-3"
              : viewMode === MarketViewMode.COMPACT
              ? "grid gap-2 sm:grid-cols-2 lg:grid-cols-4"
              : "space-y-3"
          }
        >
          {displayMarkets.map((market) => (
            <motion.div key={market.id} variants={itemVariants}>
              <Link href={`/markets/${market.id}`}>
                <MarketCard
                  market={market}
                  viewMode={viewMode}
                  isFavorite={isFavorite(market.id)}
                  onToggleFavorite={(e) => {
                    e.preventDefault();
                    toggleFavorite(market.id);
                  }}
                />
              </Link>
            </motion.div>
          ))}
        </motion.div>
      ) : (
        <Card>
          <CardContent className="p-12 text-center">
            <Sparkles className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">No markets found</h3>
            <p className="text-muted-foreground mb-4">
              {searchQuery || filters.categories?.length
                ? "Try adjusting your search or filters"
                : "Markets will appear here once loaded"}
            </p>
            {(searchQuery || filters.categories?.length) && (
              <Button variant="outline" onClick={resetFilters}>
                Clear Filters
              </Button>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

// Market Card Component
// T17.2: Memoized to prevent unnecessary re-renders in the markets grid
interface MarketCardProps {
  market: any;
  viewMode: MarketViewMode;
  isFavorite: boolean;
  onToggleFavorite: (e: React.MouseEvent) => void;
}

const MarketCard = React.memo(function MarketCard({ market, viewMode, isFavorite, onToggleFavorite }: MarketCardProps) {
  const priceChange = market.price_change_24h || 0;
  const isPriceUp = priceChange >= 0;

  if (viewMode === MarketViewMode.COMPACT) {
    return (
      <Card className="group hover:shadow-md transition-all duration-200 cursor-pointer">
        <CardContent className="p-3">
          <div className="flex items-center justify-between gap-2">
            <div className="min-w-0 flex-1">
              <p className="text-xs font-medium truncate">{market.question}</p>
              <p className="text-lg font-bold">
                {market.current_price ? `${(market.current_price * 100).toFixed(0)}%` : "N/A"}
              </p>
            </div>
