"use client"

import * as React from "react"
import { useMotionValue, useSpring, useTransform } from "framer-motion"

// ============================================================================
// TYPES
// ============================================================================

/**
 * Tilt configuration
 */
export interface TiltConfig {
  /** Maximum tilt angle in degrees */
  maxTilt?: number
  /** Enable/disable tilt */
  enabled?: boolean
  /** Enable perspective (3D effect) */
  perspective?: boolean
  /** Scale factor on hover */
  scale?: number
  /** Spring stiffness */
  stiffness?: number
  /** Spring damping */
  damping?: number
  /** Reverse tilt direction */
  reverse?: boolean
  /** Reset on mouse leave */
  resetOnLeave?: boolean
}

/**
 * Tilt values returned by useTilt hook
 */
export interface TiltValues {
  /** Rotate X value (spring-animated) */
  rotateX: React.MutableRefObject<number>
  /** Rotate Y value (spring-animated) */
  rotateY: React.MutableRefObject<number>
  /** Scale value (spring-animated) */
  scale: React.MutableRefObject<number>
  /** Current X mouse position (-1 to 1) */
  mouseX: React.MutableRefObject<number>
  /** Current Y mouse position (-1 to 1) */
  mouseY: React.MutableRefObject<number>
  /** Whether the element is hovered */
  isHovered: boolean
  /** Reset the tilt */
  reset: () => void
  /** Element ref */
  ref: React.RefObject<HTMLElement>
}

/**
 * Tilt event handlers
 */
export interface TiltHandlers {
  onMouseMove: (e: React.MouseEvent<HTMLElement>) => void
  onMouseEnter: () => void
  onMouseLeave: () => void
}

// ============================================================================
// USE TILT HOOK
// ============================================================================

/**
 * useTilt Hook
 *
 * Calculates 3D tilt based on mouse position.
 * Uses Framer Motion springs for smooth animations.
 *
 * @example
 * ```tsx
 * const { ref, rotateX, rotateY, scale, handlers } = useTilt({
 *   maxTilt: 15,
 *   scale: 1.05,
 * })
 *
 * <motion.div
 *   ref={ref}
 *   style={{ rotateX, rotateY, scale }}
 *   {...handlers}
 * >
 *   Tilt me!
 * </motion.div>
 * ```
 */
export function useTilt<T extends HTMLElement = HTMLDivElement>(
  config: TiltConfig = {}
): TiltValues & TiltHandlers {
  const {
    maxTilt = 15,
    enabled = true,
    scale = 1.05,
    stiffness = 300,
    damping = 20,
    reverse = false,
    resetOnLeave = true,
  } = config

