import React, { forwardRef, useState, useEffect } from "react"
import { cn } from "@/lib/utils"
import { applyGlassPreset, type GlassPreset } from "./glass-presets"

// ============================================================================
// TYPES
// ============================================================================

export interface GlassCardProps extends React.HTMLAttributes<HTMLDivElement> {
  /**
   * Glass effect preset
   * @default "medium"
   */
  variant?: GlassPreset | "blue" | "purple" | "green" | "amber" | "rose"

  /**
   * Enable hover elevation effect
   * @default false
   */
  hover?: boolean

  /**
   * Hover lift distance in pixels
   * @default 4
   */
  hoverLift?: number

  /**
   * Enable click animation
   * @default true
   */
  pressAnimation?: boolean

  /**
   * Card border radius
   * @default "lg"
   */
  rounded?: "none" | "sm" | "md" | "lg" | "xl" | "2xl" | "3xl" | "full"

  /**
   * Additional CSS classes
   */
  className?: string

  /**
   * Card content
   */
  children: React.ReactNode
}

// ============================================================================
// COMPONENT
// ============================================================================

/**
 * GlassCard with glassmorphism effect
 */
export const GlassCard = forwardRef<HTMLDivElement, GlassCardProps>(
  (
    {
      variant = "medium",
      hover = false,
      hoverLift = 4,
      pressAnimation = true,
      rounded = "lg",
      className,
      children,
      style,
      ...props
    },
    ref
  ) => {
    const [darkMode, setDarkMode] = useState(false)

    // Detect dark mode
    useEffect(() => {
      const root = window.document.documentElement
      const isDark = root.classList.contains("dark")
      setDarkMode(isDark)

      // Listen for theme changes
      const observer = new MutationObserver(() => {
        setDarkMode(root.classList.contains("dark"))
      })

      observer.observe(root, {
        attributes: true,
        attributeFilter: ["class"],
      })

      return () => observer.disconnect()
    }, [])

    // Get glass styles
    const glassStyles = applyGlassPreset(variant, { darkMode })

    // Border radius mapping
    const roundedClasses: Record<string, string> = {
      none: "rounded-none",
      sm: "rounded-sm",
      md: "rounded-md",
      lg: "rounded-lg",
      xl: "rounded-xl",
      "2xl": "rounded-2xl",
      "3xl": "rounded-3xl",
      full: "rounded-full",
    }

    // Base classes
    const baseClasses = [
      "overflow-hidden",
      "transition-all",
      "duration-200",
      "ease-out",
      "will-change-transform",
      "will-change-[box-shadow]",
    ]

    // Hover classes
    const hoverClasses = hover
      ? [
          "hover:shadow-xl",
          "hover:-translate-y-[4px]",
          pressAnimation ? "active:scale-[0.98]" : "",
        ]
      : []

    return (
      <div
        ref={ref}
        className={cn(
          baseClasses,
          roundedClasses[rounded],
          hoverClasses,
          className
        )}
        style={{
          ...glassStyles,
          ...style,
          ...(hover && {
            "--hover-lift": `${-hoverLift}px`,
          } as React.CSSProperties),
        }}
        {...props}
      >
        {children}
      </div>
    )
  }
)

