  'https://xyzcompany.supabase.co',
  'your-anon-key',
  {
    accessToken: async () => {
      // Return JWT from your third-party auth provider
      // This function is called automatically for each request
      const token = await myAuthProvider.getAccessToken()
      return token
    }
  }
